INSERT INTO `appqueries` (`id`, `querycode`, `description`, `query`, `dateentered`) VALUES
(1, 'user_login', 'Picks an active user''s details who matches the pair _USERNAME_ and _PASSWORD_', 'SELECT\r\n  U.userid,\r\n  U.username,\r\n  U.isadmin,\r\n  U.password,\r\n  U.usergroup,\r\n  U.emailaddress,\r\n  U.firstname,\r\n  U.middlename,\r\n  U.lastname,\r\n  U.photo,\r\n  U.pde,\r\n  R.groupid,\r\n  UG.groupname\r\nFROM users U\r\n  LEFT OUTER JOIN roles R\r\n    ON U.userid = R.userid\r\n  LEFT OUTER JOIN usergroups UG\r\n    ON R.groupid = UG.id WHERE U.emailaddress = ''_EMAILADDRESS_'' AND U.password = ''_PASSWORD_'' AND U.isactive = ''Y'' AND R.isactive = ''Y''\r\nLIMIT 1', '2010-10-19 13:13:13'),
(2, 'delete_old_captchas', 'Deletes old captcha images', 'DELETE FROM captcha WHERE captcha_time < ''_OLDESTTIME_''', '2011-02-11 19:46:11'),
(3, 'check_for_captcha', 'Checks for a captcha image from its database table', 'SELECT COUNT(*) AS count FROM captcha WHERE word = ''_THEWORD_'' AND ip_address = ''_THISIP_'' AND captcha_time > ''_OLDESTDATE_''', '2011-02-11 19:49:16'),
(4, 'insert_captcha_record', 'Add a captcha record', 'INSERT INTO captcha (captcha_time, ip_address, word) VALUES (''_CAPTCHA_TIME_'', ''_IP_ADDRESS_'', ''_WORD_'')', '2012-10-17 21:35:45'),
(5, 'save_access_trail', 'Save access trail', 'INSERT INTO ppda.accesstrail\r\n(\r\n  url\r\n ,emailaddress\r\n ,logresult\r\n ,ipaddress\r\n ,browser\r\n ,action\r\n ,logstamp\r\n ,countrycode\r\n ,countryname\r\n ,flag\r\n ,city\r\n ,region\r\n ,isp\r\n ,latitude\r\n ,longitude\r\n)\r\nVALUES\r\n(\r\n  ''_URL_''\r\n ,''_EMAILADDRESS_''\r\n ,''_LOGRESULT_''\r\n ,''_IPADDRESS_''\r\n ,''_BROWSER_''\r\n ,''_ACTION_''\r\n ,''_LOGSTAMP_''\r\n ,''_COUNTRYCODE_''\r\n ,''_COUNTRYNAME_''\r\n ,''_FLAG_''\r\n ,''_CITY_''\r\n ,''_REGION_''\r\n ,''_ISP_''\r\n ,''_LATITUDE_''\r\n ,''_LONGITUDE_''\r\n)', '2012-10-17 22:19:00'),
(6, 'get_testimonials_list', 'Get testimonials list', 'SELECT * FROM testimonials WHERE isactive IN (_ISACTIVE_) ORDER BY dateadded DESC _LIMITTEXT_', '2012-10-17 22:41:19'),
(7, 'update_testimonial_status', 'Update the testimonial status', 'UPDATE testimonials SET _FIELD_=''_VALUE_'' WHERE id=''_ID_''', '2012-10-17 19:06:04'),
(8, 'delete_testimonial', 'Delete testimonial', 'DELETE FROM testimonials WHERE id=''_ID_''', '2012-10-17 23:03:43'),
(9, 'get_user_by_id', 'Get a user by ID', 'SELECT U.*, P.pdename FROM users U LEFT JOIN pdes P ON U.pde = P.pdeid WHERE userid = ''_ID_''', '2012-10-18 21:56:08'),
(10, 'update_user_data', 'Update the user data', 'UPDATE users SET firstname=''_FIRSTNAME_'', lastname=''_LASTNAME_'', middlename=''_MIDDLENAME_'', \r\naddressline1=''_ADDRESSLINE1_'',emailaddress=''_EMAILADDRESS_'', telephone=''_TELEPHONE_'', pde=''_PDE_'', username=''_USERNAME_'', prefix=''_PREFIX_'', gender=''_GENDER_'', isadmin=''_ISADMIN_'', usergroup=''_USERGROUP_'' _UPDATECOND_ WHERE userid=''_EDITID_''', '2012-10-18 22:22:16'),
(11, 'add_student', 'Saves a new student', 'INSERT INTO students (firstname, middlename,lastname, school, studentno, gender, dob, photo,admissionterm,admissionclass,guardianone,guardianonegender, guardianoneoccupation, guardianoneaddress,guardiantwo,guardiantwotelephone,guardiantworelationship,guardiantwogender, guardiantwooccupation, guardiantwoaddress,author) VALUES (''_FIRSTNAME_'',''_MIDDLENAME_'',''_LASTNAME_'',''_SCHOOL_'',''_STUDENTNO_'',''_GENDER_'',''_DOB_'',''_PHOTO_'',''_ADMISSIONTERM_'',''_ADMISSIONCLASS_'',''_GUARDIANONE_'',''_GUARDIANONEGENDER_'',''_GUARDIANONEOCCUPATION_'',''_GUARDIANONEADDRESS_'',''_GUARDIANTWO_'',''_GUARDIANTWOTELEPHONE_'',''_GUARDIANTWORELATIONSHIP_'',''_GUARDIANTWOGENDER_'',''_GUARDIANTWOOCCUPATION_'',''_GUARDIANTWOADDRESS_'',''_AUTHOR_'')', '2012-11-14 00:00:00'),
(12, 'get_user_by_email', 'Gets an active user given their email', 'SELECT * FROM users WHERE emailaddress=''_EMAILADDRESS_'' AND isactive=''_ISACTIVE_''', '2012-10-27 13:13:27'),
(13, 'update_user_password', 'Updates a user password', 'UPDATE users SET password=''_NEWPASS_'' WHERE emailaddress=''_EMAILADDRESS_'' AND isactive=''Y''', '2012-10-27 13:14:12'),
(14, 'add_borroweditems', 'Adds item in each borrower object', 'INSERT INTO borroweditems(borrower, item) VALUES(''_BORROWER_'',''_ITEM_'');', '2013-04-04 00:00:00'),
(15, 'update_item_borrowed', 'Updates availability status for a given item', 'UPDATE library SET isavailable=''_ISAVAILABLE_'' WHERE id=''_ID_''', '2013-04-04 00:00:00'),
(16, 'get_rental_data', 'Gets data about a given borrower', 'SELECT *,b.id as borrower FROM borrowers b, students s, librarystock ls WHERE b.studentid=s.id AND b.stockid=ls.id AND b.id=''_ID_''', '2013-04-04 00:00:00'),
(17, 'update_miscelleneous', 'Updates miscelleneous message', 'UPDATE miscelleneous SET subject=''_SUBJECT_'',message=''_MESSAGE_'',eval=''_EVAL_'' WHERE id=''_ID_''', '2013-03-31 00:00:00'),
(18, 'get_miscelleneous_list', 'Gets the miscelleneous student messages', 'SELECT *,m.id as miscid,s.id as studentid FROM students s JOIN miscelleneous m ON s.id = m.student WHERE _SEARCHSTRING_ ORDER BY m.dateadded DESC', '2013-03-31 00:00:00'),
(19, 'get_miscelleneous_by_id', 'Gets miscelleneous by specified id', 'SELECT * FROM miscelleneous WHERE id=''_ID_''', '2013-03-31 00:00:00'),
(20, 'get_deskid_list', 'Get the desk id number', 'SELECT firstname, lastname, deskid FROM users WHERE deskid != '''' AND isactive=''Y'' AND _SEARCHSTRING_ ORDER BY deskid _LIMITTEXT_', '2012-11-23 14:22:27'),
(21, 'get_stock_items_list', 'Gets stock items list', 'SELECT *,l.createdon as datecreated,l.id as itemid FROM library l JOIN librarystock s ON l.bookid = s.id WHERE _SEARCHSTRING_ ORDER BY l.createdon DESC', '2013-03-28 00:00:00'),
(22, 'search_general_user_list', 'Search general user list', 'SELECT U.*, O.organizationname FROM users U LEFT JOIN organizations O ON (O.id=U.companyid) WHERE U.isactive=''Y''  AND U.usertype IN (''QC'',''QIB'',''AC'') AND U.isadmin = ''N'' _SEARCHSTRING_ ORDER BY U.username _LIMITTEXT_', '2012-11-27 17:53:54'),
(23, 'add_miscelleneous', 'Adds miscelleneous message', 'INSERT INTO miscelleneous(eval,school,student, author, subject, message) VALUES(''_EVAL_'',''_SCHOOL_'',''_STUDENT_'',''_AUTHOR_'',''_SUBJECT_'',''_MESSAGE_'')', '2013-03-31 00:00:00'),
(24, 'get_item_transaction', 'Gets transaction history for a particular item', 'SELECT * FROM transactions t JOIN students s ON s.id = t.studentid  WHERE itemid=''_ITEMID_''', '2013-03-28 00:00:00'),
(25, 'get_user_by_username', 'Get user by username', 'SELECT * FROM users WHERE username=''_USERNAME_''', '2012-11-29 22:12:29'),
(26, 'get_student_by_id', 'Gets student data for a particular id', 'SELECT * FROM students WHERE id=''_ID_''', '2013-03-20 00:00:00'),
(27, 'get_inventory_by_id', 'Gets inventory data given a particular id', 'SELECT * FROM inventory WHERE id=''_ID_''', '2013-03-19 00:00:00'),
(28, 'get_transaction_list', 'Gets data of all transactions', 'SELECT *,t.id AS transactionid,s.id as studentid,i.id AS itemid FROM transactions t, students s,items i WHERE _SEARCHSTRING_ AND s.id = t.studentid AND t.itemid = i.id GROUP BY t.id ORDER BY datecreated DESC _LIMITTEXT_', '2013-03-20 00:00:00'),
(29, 'get_options', 'Gets data for populating an option list', 'SELECT * FROM _TABLE_ _SEARCHSTRING_', '2013-03-20 00:00:00'),
(30, 'get_stocked', 'Gets the sum of quantity of a particular item', 'SELECT SUM(quantity) as total FROM inventory WHERE itemid = ''_ID_''', '2013-03-20 00:00:00'),
(31, 'add_stock', 'Adds library stock', 'INSERT INTO librarystock(school,sectionid,stocknumber,stocktitle,author,createdby) VALUES(''_SCHOOL_'',''_SECTIONID_'',''_STOCKNUMBER_'',''_STOCKTITLE_'',''_AUTHOR_'',''_CREATEDBY_'')', '2013-03-20 00:00:00'),
(32, 'get_transaction_by_id', 'Gets a transaction of a given id', 'SELECT * FROM transactions t JOIN items i ON t.itemid = i.id WHERE t.id=''_ID_''', '2013-03-20 00:00:00'),
(33, 'update_transaction', 'Updates given transactions', 'UPDATE TRANSACTIONS SET itemid=''_ITEMID_'',datecreated=''_DATECREATED_'',quantity=''_QUANTITY_'',amount=''_AMOUNT_'' WHERE id=''_ID_''', '2013-03-20 00:00:00'),
(34, 'get_user_list', 'get the user list', 'SELECT U.* FROM users U WHERE U.isactive=''Y'' _SEARCHSTRING_ ', '2012-11-21 00:00:00'),
(35, 'insert_news_article', 'Create a news article', 'INSERT INTO `news` (`title`,`articlelink`,`hasvideo`,`affectedfund`,`newsdate`,`newstime`,`addedby`) VALUES (''_TITLE_'',''_ARTICLELINK_'',''_HASVIDEO_'',''_AFFECTEDFUND_'',''_NEWSDATE_'',''_NEWSTIME_'',''_ADDEDBY_'')', '2012-11-30 00:00:00'),
