(416, 'update_sponsor_info', 'Edit a sponsor''s basic data', 'UPDATE sponsors SET `firstname`=''_FIRSTNAME_'',`lastname`=''_LASTNAME_'',`middlename`=''_MIDDLENAME_'',`gender`=''_GENDER_'',`dob`=''_DOB_'',`nationality`=''_NATIONALITY_'',`address`=''_ADDRESS_'',`address2`=''_ADDRESS2_'',`telephone`=''_TELEPHONE_'',`emailaddress`=''_EMAILADDRESS_'',`author`=''_AUTHOR_'' _UPDATESTRING_ WHERE sponsorid = ''_EDITID_''', '2012-11-14 00:00:00'),
(417, 'get_student_sponsor_list', 'Get the students list', 'SELECT students.*, CONCAT(sponsors.firstname, '' '', sponsors.lastname) AS sponsorfullname, sponsorid FROM students LEFT JOIN studentsponsors ON students.id = studentsponsors.student LEFT JOIN sponsors ON studentsponsors.sponsor = sponsors.sponsorid WHERE students.isactive =''_ISACTIVE_'' AND (studentsponsors.isactive=''Y'' OR studentsponsors.isactive=''Y'' IS NULL)  AND (studentsponsors.iscurrent=''Y'' OR studentsponsors.iscurrent IS NULL)  _SEARCHSTRING_ ORDER BY firstname,lastname _LIMITTEXT_', '2012-11-29 22:12:29'),
(418, 'search_classes_register', 'get classes plus the number of registered students for a selected tern', 'SELECT classes.id AS classid,classes.dateadded as classdateadded, classes.class as classname, numOfStudents, rank FROM classes LEFT OUTER JOIN (SELECT count(*) as numOfStudents, class FROM register WHERE term = _TERM_ GROUP BY Class) R ON R.class = classes.id WHERE classes.school = _SCHOOL_ ORDER BY classes.rank DESC _LIMITTEXT_', '2012-12-12 13:34:57'),
(419, 'school_accounts_overview', 'view account by account financial overview', 'SELECT fee_structure.fee as feename, fee_structure.id AS feeid, totalDebit, totalCredit FROM \r\n(SELECT fee, SUM(amount) as totalDebit FROM studentaccounts WHERE school = _SCHOOL_ AND type = ''DEBIT'' GROUP BY fee) AS D LEFT OUTER JOIN fee_structure ON fee_structure.id = D.fee LEFT OUTER JOIN\r\n(SELECT fee, SUM(amount) as totalCredit FROM studentaccounts WHERE school = _SCHOOL_ AND type = ''CREDIT'' GROUP BY fee) AS C ON fee_structure.id = C.fee\r\nWHERE fee_structure.school = _SCHOOL_ _SEARCHSTRING_\r\nGROUP BY fee_structure.fee', '2014-03-13 00:00:00'),
(420, 'get_type', 'get borrowers list', 'SELECT * FROM stocktransactions WHERE isactive =''_ISACTIVE_'' _SEARCHSTRING_ ORDER BY id _LIMITTEXT_', '0000-00-00 00:00:00'),
(421, 'get_stocklist_by_school', 'get the list of stock from a school', 'SELECT * FROM librarystock WHERE school=''_SCHOOL_'' ORDER BY startdate DESC', '0000-00-00 00:00:00'),
(422, 'get_stock', 'get the stock', 'SELECT * FROM librarystock ORDER BY id DESC', '0000-00-00 00:00:00'),
(423, 'search_isbn_list', 'Search through list of isbn numbers', 'SELECT * FROM library WHERE isactive = ''_ISACTIVE_'' _SEARCHSTRING_ ORDER BY isbnnumber _LIMITTEXT_', '0000-00-00 00:00:00'),
(424, 'get_sections', 'Get all section data', 'SELECT * FROM sections ORDER BY id DESC', '0000-00-00 00:00:00'),
(425, 'get_stock_by_school', 'get stock that belongs to a school', 'SELECT * FROM stock WHERE school=''_SCHOOL_'' ORDER BY startdate DESC', '0000-00-00 00:00:00'),
(426, 'search_titles', 'Search through the list of titles', 'SELECT * FROM librarystock WHERE _SEARCHSTRING_ ORDER BY stocktitle _LIMITTEXT_', '2014-03-24 00:00:00'),
(427, 'insert_stock', 'insert new stock into the library', 'INSERT INTO `library` (`stockid`,`isbnnumber`,`serialnumber`, `dateadded`) VALUES (''_STOCKID_'',''_ISBNNUMBER_'',''_SERIALNUMBER_'', ''_DATEADDED_'')', '0000-00-00 00:00:00'),
(428, 'search_library', 'Search the library', 'SELECT * FROM library WHERE _SEARCHSTRING_', '2014-03-21 00:00:00'),
(429, 'get_num_of_book_transactions', 'GET NUMBER OF AVAILABLE COPIES OF A SELECTED BOOK', 'SELECT COUNT(*) as availableBooks FROM library WHERE id IN (SELECT id FROM librarytransactions WHERE id IN (SELECT id FROM librarytransactions WHERE transactiontype = ''RETURN'')) AND bookid = _BOOKID_', '2014-03-21 00:00:00'),
(430, 'add_library_transaction', 'Add library transaction', 'CALL add_library_transaction(_STOCKID_, _BORROWER_, ''_BORROWERTYPE_'', ''_DATEBORROWED_'', ''_EXPECTEDRETURNDE_'',\r\n''_RETURNDATE_'',''_TRANSACTIONTYPE_'',''_AUTHOR_,''_COMMENTS_'', ''_STATUS_'')', '2014-03-21 00:00:00'),
(431, 'search_students_users', 'Searches through a list of teachers and students', '(SELECT schoolusers.id AS userid, schoolusers.firstname AS userfirstname, schoolusers.lastname AS userlastname, "Staff" AS typeOfUser\r\nFROM schoolusers\r\nWHERE schoolusers.isactive = ''Y'' AND schoolusers.school = _SCHOOL_ AND (firstname like ''%_FIRSTNAME_%'' OR lastname like ''%_LASTNAME_%'') _LIMITTEXT_)\r\n\r\nUNION ALL\r\n\r\n(SELECT students.id AS userid, students.firstname AS userfirstname, students.lastname AS userlastname, "Student" AS typeOfUser\r\nFROM students\r\nWHERE students.isactive = ''Y'' AND students.school = _SCHOOL_ AND (firstname like ''%_FIRSTNAME_%'' OR lastname like ''%_LASTNAME_%'') _LIMITTEXT_) ORDER BY userfirstname', '2014-03-24 00:00:00'),
(432, 'search_library_with_titles', 'Gets the book isbn numbers along with their corresponding titles and author info', 'SELECT\r\n    `library`.`isbnnumber`\r\n    , `library`.`status` AS bookStatus\r\n    , `librarystock`.`stocktitle` AS bookTitle\r\n    , `librarystock`.`author` AS bookAuthor\r\n    , `library`.`id` AS booklibraryId\r\nFROM\r\n    `academia`.`librarystock`\r\n    INNER JOIN `academia`.`library` \r\n        ON (`librarystock`.`id` = `library`.`bookid`)\r\nWHERE librarystock.school = ''_SCHOOL_'' \r\nAND (isbnnumber LIKE ''%_ISBNNUMBER_%'' OR stocktitle LIKE ''%_STOCKTITLE_%'' OR author LIKE ''%_AUTHOR_%'') _SEARCHSTRING_ _LIMITTEXT_', '2014-03-24 00:00:00'),
(433, 'add_library_transaction_headers', 'adds a library transaction''s headers', 'INSERT INTO  librarytransactionheaders (borrowerid, borrowertype,author, school) VALUES (''_BORROWERID_'', ''_BORROWERTYPE_'',''_AUTHOR_'', ''_SCHOOL_'')', '2012-11-14 00:00:00'),
(434, 'add_library_transaction_details', 'Inserts library transaction details', 'INSERT INTO `librarytransactions`\r\n            (`batchno`,\r\n            `stockid`,\r\n             `expectedreturndate`,\r\n             `transactiontype`)\r\n	VALUES _VALUES_', '2014-03-26 00:00:00'),
(435, 'update_library_stock_status', 'Updates the status of a book in the library', 'UPDATE  library SET  status = ''_STATUS_'' WHERE id IN (_BOOKS_)', '2012-11-14 00:00:00'),
(436, 'search_library_transactions_with_titles', 'Search for a book''s last borrow and return transactions', 'SELECT \r\n  latestBorrowTrans.typeBorrow \r\n  ,latestBorrowTrans.lastBorrowTransDate\r\n  ,latestReturnTrans.lastReturnTransDate\r\n  ,latestReturnTrans.typeReturn,\r\n  `library`.`isbnnumber`\r\n  ,`library`.`status` AS bookStatus\r\n  ,`librarystock`.`stocktitle` AS bookTitle\r\n  ,`librarystock`.`author` AS bookAuthor\r\n  ,`library`.`id` AS booklibraryId\r\n   \r\n  FROM `academia`.`librarystock` INNER JOIN `academia`.`library` \r\n  ON (`librarystock`.`id` = `library`.`bookid`) \r\n  LEFT JOIN ( SELECT MAX(dateadded) AS lastBorrowTransDate, stockid, librarytransactions.transactiontype  AS typeBorrow FROM librarytransactions WHERE transactiontype = ''BORROW'' \r\nGROUP BY stockid) latestBorrowTrans ON latestBorrowTrans.stockid = library.id\r\n  LEFT JOIN ( SELECT MAX(dateadded) AS lastReturnTransDate, stockid, librarytransactions.transactiontype  AS typeReturn FROM librarytransactions WHERE transactiontype = ''RETURN'' \r\nGROUP BY stockid) latestReturnTrans ON latestReturnTrans.stockid = library.id\r\n  WHERE librarystock.school = ''10'' AND (isbnnumber LIKE ''%_ISBNNUMBER_%'' OR stocktitle LIKE ''%_STOCKTITLE_%'' OR author LIKE ''%_AUTHOR_%'') _LIMITTEXT_', '2014-04-16 00:00:00'),
(437, 'search_library_transactions', 'Search library transactions', 'SELECT\r\n  librarystock.stocktitle,\r\n  library.isbnnumber,\r\n  library.bookid,\r\n  library.id AS stockid,\r\n  librarytransactions.expectedreturndate,\r\n  librarytransactionheaders.transactiondate,\r\n  librarytransactionheaders.borrowerid,\r\n  librarytransactionheaders.borrowertype,\r\n  users.firstname AS userfirstname,\r\n  users.lastname AS userlastname,\r\n  librarytransactionheaders.author AS userid,\r\nlibrarystock.author\r\nFROM librarytransactionheaders\r\n  INNER JOIN librarytransactions\r\n    ON librarytransactionheaders.headerid = librarytransactions.batchno\r\n  INNER JOIN library\r\n    ON library.id = librarytransactions.stockid\r\n  INNER JOIN librarystock\r\n    ON librarystock.id = library.bookid\r\n  INNER JOIN users\r\n    ON users.id = librarytransactionheaders.author\r\n  WHERE _SEARCHSTRING_ _ORDERBY_ _LIMITTEXT_', '2014-04-17 00:00:00'),
(438, 'add_single_library_transaction', 'Inserts single library transaction details', 'INSERT INTO `librarytransactions`\r\n            (`batchno`,\r\n            `stockid`,\r\n             `expectedreturndate`,\r\n             `transactiontype`,`returndate`,`author`,`comments`)\r\n	VALUES (''_BATCHNO_'', ''_STOCKID_'',''_EXPECTEDRETURNDATE_'', ''_TRANSACTIONTYPE_'',''_RETURNDATE_'',''_AUTHOR_'', ''_COMMENTS_'')', '2014-03-26 00:00:00'),
(587, 'search_students_by_term_and_class', 'Search for students by class and term', 'SELECT\r\n  students.*,\r\n  CONCAT(sponsors.firstname, '' '', sponsors.lastname) AS sponsorfullname,\r\n  sponsors.sponsorid,\r\n  terms.term,\r\n  terms.year,\r\n  classes.class\r\nFROM students\r\n  LEFT OUTER JOIN studentsponsors\r\n    ON students.id = studentsponsors.student\r\n  LEFT OUTER JOIN sponsors\r\n    ON studentsponsors.sponsor = sponsors.sponsorid\r\n  LEFT OUTER JOIN register\r\n    ON students.id = register.student\r\n  INNER JOIN classes\r\n    ON classes.id = register.class\r\n  INNER JOIN terms\r\n    ON terms.id = register.term\r\nWHERE students.isactive = ''Y'' \r\n  AND (studentsponsors.isactive = ''Y'' OR studentsponsors.isactive = ''Y'' IS NULL) \r\n  AND (studentsponsors.iscurrent = ''Y'' OR studentsponsors.iscurrent IS NULL) \r\n  AND students.school = _SCHOOL_ \r\n  AND (students.firstname LIKE ''%_FIRSTNAME_%'' OR students.lastname LIKE ''%_LASTNAME_%'' OR students.studentno LIKE ''%_STUDENTNO_%'' OR sponsors.firstname LIKE ''%_SPONSORFIRSTNAME_%'' OR sponsors.lastname LIKE ''%_SPONSORLASTNAME_%'')\r\n  _SEARCHSTRING_\r\nORDER BY students.firstname, students.lastname\r\n_LIMITTEXT_', '2014-05-12 16:59:11'),
(588, 'search_discipline', 'searches through the discipline table', 'SELECT\r\n  discipline.reportedby,\r\n  discipline.incidentid,\r\n  discipline.student,\r\n  discipline.incidentdate,\r\n  discipline.incidentdetails,\r\n  discipline.response,\r\n  discipline.actiontaken,\r\n  discipline.isactive,\r\n  discipline.dateadded,\r\n  discipline.author,\r\n  students.firstname AS studentfirstname,\r\n  students.middlename AS studentmiddlename,\r\n  students.lastname AS studentlastname,\r\n  CONCAT(students.firstname, '' '', students.middlename, '' '', students.lastname) AS studentfullname,\r\n  schoolusers.firstname AS userfirstname,\r\n  schoolusers.lastname AS userlastname,\r\n  schoolusers.middlename AS usermiddlename,\r\n  CONCAT(schoolusers.firstname, '' '', schoolusers.middlename, '' '', schoolusers.lastname) AS userfullname\r\nFROM discipline\r\n  INNER JOIN students\r\n    ON discipline.student = students.id\r\n  INNER JOIN schoolusers\r\n    ON discipline.reportedby = schoolusers.id\r\n  WHERE students.school = _SCHOOL_ AND _SEARCHSTRING_', '2014-06-23 11:48:57'),
(589, 'add_incident', 'Add disciplinary incidents', 'INSERT INTO discipline\r\n(\r\n  student\r\n ,reportedby\r\n ,incidentdate\r\n ,incidentdetails\r\n ,response\r\n ,actiontaken\r\n ,author\r\n)\r\nVALUES\r\n(\r\n  ''_STUDENT_''\r\n ,''_REPORTEDBY_''\r\n ,''_INCIDENTDATE_''\r\n ,''_INCIDENTDETAILS_''\r\n ,''_RESPONSE_''\r\n ,''_ACTIONTAKEN_''\r\n ,''_AUTHOR_''\r\n)', '2014-06-23 14:36:23'),
