(593, 'insertnewpde', 'INSERT INTO PDES. CREATE NEW PDE\r\n', 'INSERT INTO pdes (pdename, abbreviation,status,created_by, category,type,code,pde_roll_cat,address,tel,fax, email, AO,AO_phone,AO_email,CC,CC_phone, head_PDU,head_PDU_phone,head_PDU_email) VALUES (''_PDENAME_'',''_ABBREVIATION_'',''_STATUS_'',''_CREATEBY_'',''_CATEGORY_'',''_TYPE_'',''_CODE_'',''_PDEROLLCAT_'',''_ADDRESS_'',''_TEL_'',''_FAX_'',''_EMAIL_'',''_AO_'',''_AOPHONE_'',''_AOEMAIL_'',''_CC_'',''_CCPHONE_'',''_HEADPDU_'',''_HEADPDUPHONE_'',''_HEADPDUEMAIL_'')', '2015-01-07 00:00:00'),
(594, 'fetchpdetypes', 'Fetch All Pdetypes based on status.. Y or N .. Isactive or Innactive', 'SELECT * FROM pdetypes where isactive like ''%_STATUS_%'' _SEARCHSTRING_ _ORDERBY_ _LIMITTEXT_', '2015-01-07 00:00:00'),
(595, 'fetchpdetype', 'Fetch Pde Type', 'SELECT * FROM pdetypes where   pdetypeid =   ''_ID_''', '2015-01-07 00:00:00'),
(596, 'updatepde', 'Update Pdes of a gien individual pdeid ', 'UPDATE pdes SET pdename = ''_PDENAME_'', abbreviation = ''_ABBREVIATION_'',status=''_STATUS_'',created_by=''_CREATEBY_'', category=''_CATEGORY_'',type=''_TYPE_'',code=''_CODE_'',pde_roll_cat=''_PDEROLLCAT_'',address=''_ADDRESS_'',tel=''_TEL_'',fax=''_FAX_'', email=''_EMAIL_'', AO=''_AO_'',AO_phone=''_AOPHONE_'',AO_email=''_AOEMAIL_'',CC=''_CC_'',CC_phone=''_CCPHONE_'', head_PDU=''_HEADPDU_'',head_PDU_phone=''_HEADPDUPHONE_'',head_PDU_email=''_HEADPDUEMAIL_'' WHERE pdeid = ''_ID_''', '2015-01-07 00:00:00'),
(597, 'deletepde', 'Parmanent Delete of Pde', 'DELETE FROM pdes where pdeid = ''_ID_''', '2015-01-07 00:00:00'),
(598, 'searchpde', 'search Pdes based on any criteria.\r\nmajorly used for checks of a given data in server side validation', 'SELECT * FROM pdes WHERE _SEARCHSTRING_\r\n', '2015-01-07 00:00:00'),
(599, 'get_pde_by_id', 'Gets particular pde information by pdeid', 'SELECT * FROM pdes where pde_id = _ID_', '2015-01-22 00:00:00'),
(600, 'fetchusergroups', 'Fetch User Groups', 'SELECT * FROM usergroups where _SEARCHSTRING_', '2015-01-26 00:00:00'),
(601, 'assignedroles', 'Assigned Roles', 'select a.userid,a.groupid,a.author,a.isactive,a.dateadded,b.pde as pdeid FROM roles as a left join users as b on a.userid = b.userid  WHERE  _SEARCHSTRING_\n', '2015-01-26 00:00:00'),
(602, 'get_user_group_list', 'Get the list of user groups', 'SELECT\n  UG.groupname,\n  UG.id AS usergroupid,\n  CASE WHEN GU.numOfUsers IS NULL THEN 0 ELSE GU.numOfUsers END AS numOfUsers,\n  UG.author,\n  UG.dateadded,\n  CONCAT(users.firstname,'' '', users.lastname) AS authorname\nFROM usergroups UG\n  LEFT OUTER JOIN (SELECT\n      COUNT(*) AS numOfUsers,\n      R.groupid\n    FROM roles R INNER JOIN users u ON u.userid = R.userid\n    WHERE R.isactive = ''Y'' AND u.isactive = ''Y''\n    GROUP BY R.groupid) GU\n    ON UG.id = GU.groupid\n  INNER JOIN users\n    ON UG.author = users.userid\nWHERE _SEARCHSTRING_ _ORDERBY_ _LIMITTEXT_', '2015-01-27 00:00:00'),
(603, 'get_roles_by_user', 'Get a user''s roles', 'SELECT * FROM roles WHERE userid = ''_USERID_'' AND isactive = ''Y''', '0000-00-00 00:00:00'),
(604, 'deleteroles', 'Delete Roles', 'DELETE FROM `roles` WHERE _SEARCHSTRING_', '2015-01-28 00:00:00'),
