(609, 'add_bid_invitation', 'create a bid invitation', 'INSERT INTO bidinvitations\n(\n  bid_openning_date\n ,procurement_ref_no\n ,subject_of_procurement\n ,cost_estimate\n ,invitation_to_bid_date\n ,pre_bid_meeting_date\n ,procurement_id\n ,description_of_works\n ,bid_security_amount\n ,bid_security_currency\n ,bid_documents_price\n ,bid_documents_currency\n ,author\n ,bid_submission_deadline\n ,bid_evaluation_to\n ,bid_evaluation_from\n ,display_of_beb_notice\n ,contract_award_date\n ,bid_receipt_address\n ,vote_no\n ,initiated_by\n ,date_initiated\n ,documents_inspection_address\n ,documents_address_issue\n ,bid_openning_address\n ,dateofconfirmationoffunds\n ,bidvalidityperiod\n ,bidvalidity\n ,quantity\n)\nVALUES\n(\n  ''_BID_OPENNING_DATE_''\n ,''_PROCUREMENT_REF_NO_''\n ,''_SUBJECT_OF_PROCUREMENT_''\n ,''_COST_ESTIMATE_''\n ,''_INVITATION_TO_BID_DATE_''\n ,''_PRE_BID_MEETING_DATE_''\n ,''_PROCUREMENT_ID_''\n ,''_DESCRIPTION_OF_WORKS_''\n ,''_BID_SECURITY_AMOUNT_''\n ,''_BID_SECURITY_CURRENCY_''\n ,''_BID_DOCUMENTS_PRICE_''\n ,''_BID_DOCUMENTS_CURRENCY_''\n ,''_AUTHOR_''\n ,''_BID_SUBMISSION_DEADLINE_''\n ,''_BID_EVALUATION_TO_''\n ,''_BID_EVALUATION_FROM_''\n ,''_DISPLAY_OF_BEB_NOTICE_''\n ,''_CONTRACT_AWARD_DATE_''\n ,''_BID_RECEIPT_ADDRESS_''\n ,''_VOTE_NO_''\n ,''_INITIATED_BY_''\n ,''_DATE_INITIATED_''\n ,''_DOCUMENTS_INSPECTION_ADDRESS_''\n ,''_DOCUMENTS_ADDRESS_ISSUE_''\n ,''_BID_OPENNING_ADDRESS_''\n ,''_DATEOFCONFIRMATIONBYAO_''\n ,''_VALIDITYPERIOD_''\n ,''_VALIDITY_''\n ,''_QUANTITYIFB_''\n\n)\n', '2015-01-31 00:00:00'),
(610, 'search_table', 'Search through a table', 'SELECT * FROM _TABLE_ WHERE  _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-01-31 00:00:00'),
(611, 'procurement_plan_details', 'Get procurement plan details', 'SELECT\n  procurement_plans.financial_year,\n  procurement_plans.pde_id,\n  procurement_plans.id AS procurement_plan_id,\n  procurement_plan_entries.id AS procurement_id,\n  bidinvitations.procurement_ref_no,\n  procurement_plan_entries.funding_source AS funding_source_id,\n  procurement_plan_entries.pde_department,\n  procurement_plans.dateadded,\n  procurement_plan_entries.procurement_method AS procurement_method_id,\n  procurement_plan_entries.procurement_type AS procurement_type_id,\n  procurement_plan_entries.subject_of_procurement,\n  procurement_plan_entries.estimated_amount,\n  procurement_plan_entries.currency,\n  procurement_types.title AS procurement_type,\n  procurement_methods.title AS procurement_method,\n  funding_sources.title AS funding_source,\n  pdes.pdename,\n  bidinvitations.isapproved AS bid_approved,\n  bidinvitations.date_approved AS date_bid_approved,\n  bidinvitations.dateadded AS date_bid_added,\n  bidinvitations.approvedby AS bid_approvedby,\n  bidinvitations.invitation_to_bid_date,\n  if(receipts.providerid > 0,receipts.providerid,TRIM(TRAILING '','' FROM joint_venture.providers)) as providers ,\n\n   bidinvitations.bid_openning_date AS bidopenningdate,\n   bidinvitations.bidvalidityperiod AS bidvalidityperiod,\n   bidinvitations.bidvalidity AS bidvalidity\n\n   \n  \nFROM procurement_plans\n  INNER JOIN procurement_plan_entries\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\n  INNER JOIN procurement_types\n    ON procurement_plan_entries.procurement_type = procurement_types.id\n  INNER JOIN procurement_methods\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\n  INNER JOIN funding_sources\n    ON procurement_plan_entries.funding_source = funding_sources.id\n  INNER JOIN pdes\n    ON procurement_plans.pde_id = pdes.pdeid\n  LEFT OUTER  JOIN bidinvitations\n    ON procurement_plan_entries.id = bidinvitations.procurement_id\n  LEFT OUTER JOIN receipts \n  ON bidinvitations.id = receipts.bid_id\n  LEFT OUTER JOIN joint_venture \n  ON receipts.joint_venture = joint_venture.jv\n\n\n\n WHERE _SEARCHSTRING_   ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-01 00:00:00'),
(612, 'bid_invitation_details', 'search for bid invitation details', 'SELECT\n  procurement_plans.financial_year,\n  bidinvitations.procurement_ref_no,\n  procurement_plan_entries.funding_source AS funding_source_id,\n  procurement_plan_entries.pde_department,\n  procurement_plans.dateadded,\n  procurement_plan_entries.procurement_method AS procurement_method_id,\n  procurement_plan_entries.procurement_type AS procurement_type_id,\n  procurement_plan_entries.subject_of_procurement,\n  procurement_plan_entries.estimated_amount,\n  procurement_plan_entries.currency,\n  procurement_types.title AS procurement_type,\n  procurement_methods.title AS procurement_method,\n  funding_sources.title AS funding_source,\n  pdes.pdename,\n  bidinvitations.id AS bidinvitation_id,\n  bidinvitations.bid_openning_date,\n  bidinvitations.pde_id,\n  bidinvitations.cost_estimate,\n  bidinvitations.invitation_to_bid_date,\n  bidinvitations.pre_bid_meeting_date,\n  bidinvitations.description_of_works,\n  bidinvitations.bid_security_amount,\n  bidinvitations.bid_security_currency,\n  bidinvitations.bid_documents_price,\n  bidinvitations.author AS bid_author,\n  bidinvitations.isapproved AS bid_approved,\n  bidinvitations.dateadded AS bid_dateadded,\n  bidinvitations.approvedby AS bid_approvedby,\n  bidinvitations.isactive AS bid_isactive,\n  bidinvitations.date_approved AS bid_date_approved,\n  bidinvitations.approval_comments AS bid_approval_comments,\n  bidinvitations.bid_submission_deadline,\n  bidinvitations.bid_evaluation_to,\n  bidinvitations.bid_evaluation_from,\n  bidinvitations.display_of_beb_notice,\n  bidinvitations.contract_award_date,\n  CONCAT(users.firstname, '' '', users.lastname) AS approver_fullname,\n  COALESCE(addenda.numOfAddenda, 0) AS numOfAddenda,\n  currencies.title AS cost_estimate_currency,\n  currencies_B.title AS bid_security_currency_title\nFROM procurement_plans\n  INNER JOIN procurement_plan_entries\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\n  INNER JOIN procurement_types\n    ON procurement_plan_entries.procurement_type = procurement_types.id\n  INNER JOIN procurement_methods\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\n  INNER JOIN funding_sources\n    ON procurement_plan_entries.funding_source = funding_sources.id\n  INNER JOIN pdes\n    ON procurement_plans.pde_id = pdes.pdeid\n  INNER JOIN bidinvitations\n    ON bidinvitations.procurement_id = procurement_plan_entries.id\n  LEFT OUTER JOIN users\n    ON bidinvitations.approvedby = users.userid\n  LEFT OUTER JOIN (SELECT\n      COUNT(*) AS numOfAddenda,\n      addenda.bidid\n    FROM addenda\n    WHERE addenda.isactive = "Y"\n    GROUP BY addenda.bidid) addenda\n    ON bidinvitations.id = addenda.bidid\n  LEFT OUTER JOIN currencies\n    ON procurement_plan_entries.currency = currencies.id\n  LEFT OUTER JOIN currencies currencies_B\n    ON bidinvitations.bid_security_currency = currencies_B.id\n  WHERE _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-01 00:00:00'),
(613, 'approve_bid_invitation', 'Approve a bid invitation', 'UPDATE bidinvitations \r\nSET\r\n isapproved =  ''Y''\r\n ,date_approved = NOW() \r\n ,approvedby = _APPROVEDBY_\r\n ,approval_comments = ''_APPROVAL_COMMENTS_''\r\nWHERE\r\n  id = _BIDINVITATION_ID_', '2015-02-01 00:00:00'),
(614, 'active_procurements', 'Active Procurements', 'SELECT\n  DISTINCT     \n  procurement_plan_entries.id, \n  procurement_plans.pde_id,   \n  procurement_plans.financial_year,   \n  procurement_plan_entries.subject_of_procurement,     \n  procurement_plan_entries.procurement_type,     \n  procurement_plan_entries.procurement_method,     \n  procurement_plan_entries.pde_department,     \n  procurement_plan_entries.funding_source,    \n  bidinvitations.procurement_ref_no,    \n  procurement_plan_entries.id as procid,    \n  procurement_types.title AS proctype,     \n   \n  procurement_methods.title AS procurement_method,  \n  funding_sources.title AS fundingsource   \n  FROM    procurement_plans         \n   \n  INNER JOIN  procurement_plan_entries        \n  ON procurement_plan_entries.procurement_plan_id = procurement_plans.id \n\n   INNER JOIN   bidinvitations        \n   ON  procurement_plan_entries.id  =  bidinvitations.procurement_id \n\n\n  INNER JOIN procurement_methods    \n  ON procurement_plan_entries.procurement_method = procurement_methods.id   \n  INNER JOIN funding_sources     \n  ON procurement_plan_entries.funding_source = funding_sources.id   \n  LEFT JOIN procurement_types     \n  ON  procurement_plan_entries.procurement_type = procurement_types.id   \n  INNER JOIN users     \n  ON  procurement_plans.pde_id = users.pde \n  \n            \n  where _SEARCHSTRING_  ', '2015-02-01 00:00:00'),
(615, 'view_bidders_list', 'View Bidders List in a Given PDE', 'SELECT  DISTINCT receipts.*,  \n  bidinvitations.bid_openning_date, \n  bidinvitations.pde_id,  \n  bidinvitations.subject_of_procurement, \n  bidinvitations.cost_estimate, \n  bidinvitations.pre_bid_meeting_date, \n  bidinvitations.invitation_to_bid_date, \n  bidinvitations.procurement_ref_no,  \n  bidinvitations.description_of_works \n  FROM receipts   \n  INNER JOIN bidinvitations    \n  ON receipts.bid_id = bidinvitations.id   \n  INNER JOIN procurement_plan_entries     \n  ON bidinvitations.procurement_ref_no = procurement_plan_entries.procurement_ref_no\n  INNER JOIN procurement_plans ON  procurement_plan_entries.procurement_plan_id = procurement_plans.id\n  INNER JOIN pdes    \n  ON pdes.pdeid = procurement_plans.pde_id  \n  INNER JOIN users     \n  ON pdes.pdeid = users.pde where _SEARCHSTRING_', '2015-02-01 00:00:00'),
(616, 'view_all_bidders_list', 'View_all_Bidders_list.. PPDA.. Super Admin', 'SELECT   receipts.*,count(*) as sm,   bidinvitations.bid_openning_date,   bidinvitations.pde_id,   bidinvitations.subject_of_procurement,   bidinvitations.cost_estimate,   bidinvitations.pre_bid_meeting_date,   bidinvitations.invitation_to_bid_date,   bidinvitations.procurement_ref_no,   bidinvitations.description_of_works FROM receipts   INNER JOIN bidinvitations     ON receipts.bid_id = bidinvitations.id   INNER JOIN procurement_plan_entries     ON bidinvitations.procurement_ref_no = procurement_plan_entries.procurement_ref_no   INNER JOIN procurement_plans on procurement_plan_entries.procurement_plan_id = procurement_plans.id  INNER JOIN pdes     ON pdes.pdeid = procurement_plans.pde_id   INNER JOIN users     ON pdes.pdeid = users.pde where _SEARCHSTRING_', '0000-00-00 00:00:00'),
(617, 'fetchbidinfo', 'fetchbidinfo', 'select * from bidinvitations where  _SEARCHSTRING_ ', '2015-02-01 00:00:00'),
(618, 'fetch_evaluation_methods', 'fetch evaluation methods', 'SELECT * FROM `evaluation_methods`   where _SEARCHSTRING_', '0000-00-00 00:00:00'),
(619, 'fetch_receipted_providers', 'fetch providers that invited bids for a given  bid invitation', ' SELECT DISTINCT  \n  receipts.receiptid,\n  receipts.providerid, \n  bidinvitations.procurement_ref_no,  \n  receipts.details,   receipts.datereceived, \n  receipts.received_by,  receipts.author,     \n  receipts.dateadded,   receipts.nationality,  \n  receipts.beb    \n  FROM bidinvitations  \n  INNER JOIN receipts    \n  ON bidinvitations.id = receipts.bid_id \n  INNER JOIN providers    \n  ON receipts.providerid = providers.providerid \n  INNER JOIN users    \n  ON users.pde = bidinvitations.pde_id \n  _SEARCHSTRING_\nUNION\n  SELECT DISTINCT  \n  receipts.receiptid,\n  joint_venture.providers,   \n  bidinvitations.procurement_ref_no,  \n  receipts.details,   receipts.datereceived, \n  receipts.received_by,  receipts.author,     \n  receipts.dateadded,   receipts.nationality,  \n  receipts.beb    \n  FROM bidinvitations  \n  INNER JOIN receipts    \n  ON bidinvitations.id = receipts.bid_id \n  INNER JOIN joint_venture    \n  ON receipts.joint_venture = joint_venture.jv \n  INNER JOIN users    \n  ON users.pde = bidinvitations.pde_id \n     _SEARCHSTRING_', '0000-00-00 00:00:00'),
(620, 'initiate_procurement', 'initiate procurement', 'INSERT INTO initiate_procurements\r\n(\r\n  procurement_ref_no\r\n ,vote_no\r\n ,initiated_by\r\n ,date_initiated\r\n ,approval_status\r\n ,author\r\n)\r\nVALUES\r\n(\r\n  ''_PROCUREMENT_REF_NO_''\r\n ,''_VOTE_NO_''\r\n ,''_INITIATED_BY_''\r\n ,''_DATE_INITIATED_''\r\n ,''_APPROVAL_STATUS_''\r\n ,''_AUTHOR_''\r\n)', '2015-02-03 00:00:00'),
(621, 'award_contract', 'Inserts awarded contract or contract signing and provider information.', 'INSERT INTO contracts\r\n(\r\n  procurement_ref_id,\r\n  provider_id,\r\n  admin_review,\r\n  date_of_sg_approval,\r\n  date_signed,\r\n  commencement_date,\r\n  completion_date,\r\n  author\r\n)\r\nVALUES\r\n(\r\n ''_PREFID_''\r\n ,''_PROVIDERID_''\r\n ,''_ISADMIN_''\r\n ,''_SGAPPROVAL_''\r\n ,''_DATE_SIGNED_''\r\n ,''_COMMENCEMENT_DATE_''\r\n ,''_COMPLETION_DATE_''\r\n ,''_AUTHOR_''\r\n)', '2015-02-01 00:00:00'),
(622, 'get_award_contract_by_name', 'Checks it there is a similar awarded contract in the contracts table', 'SELECT * FROM contracts WHERE procurement_ref_no = ''_PREFID_'' AND isactive = ''Y''', '2015-02-01 00:00:00'),
(623, 'get_awarded_contract_list', 'Gets all awarded or signed contracts', 'SELECT\r\n  UG.groupname,\r\n  UG.id AS usergroupid,\r\n  CASE WHEN GU.numOfUsers IS NULL THEN 0 ELSE GU.numOfUsers END AS numOfUsers,\r\n  UG.author,\r\n  UG.dateadded,\r\n  CONCAT(users.firstname,'' '', users.lastname) AS authorname\r\nFROM usergroups UG\r\n  LEFT OUTER JOIN (SELECT\r\n      COUNT(*) AS numOfUsers,\r\n      u.usergroup\r\n    FROM users u\r\n    WHERE u.isactive = ''Y''\r\n    GROUP BY u.usergroup) GU\r\n    ON UG.id = GU.usergroup\r\n  INNER JOIN users\r\n    ON UG.author = users.userid\r\nWHERE _SEARCHSTRING_ _ORDERBY_ _LIMITTEXT_', '0000-00-00 00:00:00'),
(624, 'get_signed_contracts', 'Show all awarded contracts online', 'SELECT \r\n  procurement_plan_entries.procurement_ref_no,\r\n  procurement_plan_entries.contract_award,\r\n  procurement_plan_entries.subject_of_procurement,\r\n  procurement_types.title AS procurement_type,\r\n  procurement_methods.title AS procurement_method,\r\n  funding_sources.title AS funding_source,\r\n  pdes.pdename,\r\n  contracts.id,\r\n  contracts.commencement_date,\r\n  contracts.contract_amount,\r\n  contracts.completion_date,\r\n  contracts.amount_currency,\r\n  contracts.dateawarded\r\n  \r\n  FROM procurement_plans\r\n  INNER JOIN procurement_plan_entries\r\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\r\n  INNER JOIN procurement_types\r\n    ON procurement_plan_entries.procurement_type = procurement_types.id\r\n  INNER JOIN procurement_methods\r\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\r\n  INNER JOIN funding_sources\r\n    ON procurement_plan_entries.funding_source = funding_sources.id\r\n  INNER JOIN pdes\r\n    ON procurement_plans.pde_id = pdes.pdeid\r\n  LEFT OUTER JOIN initiate_procurements\r\n    ON procurement_plan_entries.procurement_ref_no = initiate_procurements.procurement_ref_no\r\n  LEFT OUTER JOIN bidinvitations\r\n    ON procurement_plan_entries.procurement_ref_no = bidinvitations.procurement_ref_no\r\n  LEFT OUTER JOIN contracts\r\n  	ON procurement_plan_entries.procurement_ref_no = contracts.procurement_ref_no WHERE contracts.isactive = ''Y''  AND initiate_procurements.isactive = ''Y''', '2015-02-02 00:00:00'),
(625, 'get_published_contracts', 'This returns all active awarded contracts for the administrator', 'SELECT\n  PPE.subject_of_procurement,\n  BI.procurement_ref_no,\n  C.id,\n  C.final_award_notice_date,\n  C.date_signed,\n  C.dateadded,\n  C.actual_completion_date,\n  C.completion_date,\n  CONCAT(users.firstname, '' '', users.lastname) AS authorname,\n  PPE.procurement_plan_id,\n  PP.pde_id,\n  PP.financial_year,\n  pdes.pdename,\n  pdes.abbreviation,\n  COALESCE(total_price.price, 0) AS total_price,\n  COALESCE(total_payments_made.total_amount_paid, 0) AS total_amount_paid,\n  P.providernames,\n  C.commencement_date,\n  R.providerid,\n  R.joint_venture,\n  C.final_contract_value_currency,\n  C.final_contract_value\nFROM     procurement_plan_entries PPE\n       INNER JOIN contracts C\n         ON PPE.id = C.procurement_ref_id\n       LEFT OUTER JOIN users\n         ON users.userid = C.author\n       INNER JOIN procurement_plans PP\n         ON PPE.procurement_plan_id = PP.id\n       INNER JOIN pdes\n         ON PP.pde_id = pdes.pdeid\n  INNER JOIN  procurement_types PTY\n  ON PPE.procurement_type =  PTY.id\n  INNER JOIN  procurement_methods PMS\n  ON  PPE.procurement_method = PMS.id \n\n    \n\n       LEFT OUTER JOIN (SELECT\n           SUM(COALESCE(CP.amount, 1) * COALESCE(CP.xrate, 1)) AS price,\n           CP.contract_id\n         FROM contract_prices CP\n         WHERE CP.isactive = ''Y''\n         GROUP BY CP.contract_id) total_price\n         ON C.id = total_price.contract_id\n      INNER JOIN bidinvitations BI ON C.procurement_ref_id = BI.procurement_id\nINNER JOIN\n  (SELECT r.bid_id, r.providerid, r.joint_venture FROM receipts r WHERE r.beb = "Y" AND r.isactive = "Y") R\n    ON R.bid_id = BI.id\n    LEFT JOIN providers P ON R.providerid = P.providerid\n  LEFT JOIN (SELECT\n  SUM(COALESCE(CTP.amount, 1) * COALESCE(CTP.xrate, 1)) AS total_amount_paid, contract_id\nFROM contracts_total_payments CTP\n  WHERE CTP.isactive = ''Y''\n  GROUP BY CTP.contract_id) total_payments_made  \n  ON C.id = total_payments_made.contract_id WHERE C.isactive = ''Y'' \n  \n  \n  \n  \n  _SEARCHSTRING_\nORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-02 00:00:00'),
(626, 'fetchproviders_pde', 'Fetch Providers invited to BID in a given PDE', '  SELECT DISTINCT\n    receipts.receiptid,       \n    bidinvitations.procurement_ref_no,   \n    procurement_plan_entries.subject_of_procurement,  \n    providers.providernames as providernames, \n    receipts.datereceived,   \n    receipts.received_by,   \n    receipts.nationality,     \n    receipts.dateadded,    \n    receipts.beb , \n    receipts.readoutprice ,     \n    joint_venture.providers as joint_venture ,\n    joint_venture.provider_lead as provider_lead ,\n	  bidinvitations.id as bidid,\n    b.pdename\n  \n    FROM receipts     INNER JOIN providers     \n    ON receipts.providerid = providers.providerid  \n    INNER JOIN bidinvitations      \n    ON receipts.bid_id = bidinvitations.id      \n    INNER JOIN procurement_plan_entries        \n    ON bidinvitations.procurement_id = procurement_plan_entries.id    \n    INNER JOIN procurement_plans a       ON procurement_plan_entries.procurement_plan_id = a.id   \n    INNER JOIN pdes b     ON a.pde_id = b.pdeid    \n    INNER JOIN users  c   ON c.pde = b.pdeid  \n    INNER JOIN  procurement_plans \n    ON procurement_plan_entries.procurement_plan_id = procurement_plans.id\n    INNER JOIN pdes \n    ON procurement_plans.pde_id = pdes.pdeid\n    LEFT OUTER JOIN  joint_venture ON joint_venture.jv = receipts.joint_venture\n    WHERE 1 and 1      _SEARCHSTRING1_ \n \n  UNION\n\n  SELECT DISTINCT \n  receipts.receiptid, \n  bidinvitations.procurement_ref_no,\n  procurement_plan_entries.subject_of_procurement,   \n joint_venture.providers as providernames, \n    receipts.datereceived,   \n    receipts.received_by,   \n    receipts.nationality,     \n    receipts.dateadded,    \n    receipts.beb ,  \n    receipts.readoutprice , \n    joint_venture.providers as joint_venture ,\n    joint_venture.provider_lead as provider_lead ,\n	bidinvitations.id as bidid,\n    b.pdename\n  FROM joint_venture\n  INNER JOIN receipts\n  ON joint_venture.jv = receipts.joint_venture\n  INNER JOIN bidinvitations\n  ON receipts.bid_id = bidinvitations.id\n   INNER JOIN procurement_plan_entries        \n    ON bidinvitations.procurement_id = procurement_plan_entries.id    \n    INNER JOIN procurement_plans a       ON procurement_plan_entries.procurement_plan_id = a.id   \n    INNER JOIN pdes b     ON a.pde_id = b.pdeid    \n    INNER JOIN users  c   ON c.pde = b.pdeid  \n    INNER JOIN  procurement_plans \n    ON procurement_plan_entries.procurement_plan_id = procurement_plans.id\n    INNER JOIN pdes \n    ON procurement_plans.pde_id = pdes.pdeid\n  where receipts.joint_venture != ''''    _SEARCHSTRING2_', '2015-02-05 00:00:00'),
(627, 'insertbeb', 'insertbeb', 'INSERT INTO \nbestevaluatedbidder \n(pid,bidid,type_oem,ddate_octhe,num_orb,date_oce_r,date_oaoterbt_cc,bebid,\n nationality,contractprice,currency,ispublished,seerialnumber)\nVALUES\n(''_PID_'',''_BIDID_'',''_EVALUATIONMETHOD_'',''_DATEOFCOMMENCEMENT_'',''_NUMBIDS_'',''_DOBEVALUATION_'',''DATECC'',''_BEBNAME_'',\n ''_BEBNATIONALITY_'',''_CONTRACTPRICE_'',''_CURRENCY_'',''_ISPUBLISHED_'',''_SERIALNUMBER_'')  ', '2015-02-09 00:00:00'),
(628, 'procurement_plan_details_advanced', 'Get detailed procurement plan details', 'SELECT\r\n  procurement_plans.financial_year,\r\n  procurement_plans.pde_id,\r\n  procurement_plans.id AS procurement_id,\r\n  procurement_plan_entries.procurement_ref_no,\r\n  procurement_plan_entries.funding_source AS funding_source_id,\r\n  procurement_plan_entries.pde_department,\r\n  procurement_plans.dateadded AS date_procurement_added,\r\n  procurement_plan_entries.procurement_method AS procurement_method_id,\r\n  procurement_plan_entries.procurement_type AS procurement_type_id,\r\n  procurement_plan_entries.subject_of_procurement,\r\n  procurement_plan_entries.estimated_amount,\r\n  procurement_plan_entries.currency,\r\n  procurement_types.title AS procurement_type,\r\n  procurement_methods.title AS procurement_method,\r\n  funding_sources.title AS funding_source,\r\n  pdes.pdename,\r\n  initiate_procurements.vote_no,\r\n  initiate_procurements.date_approved,\r\n  initiate_procurements.date_initiated,\r\n  initiate_procurements.approved_by,\r\n  initiate_procurements.approval_status AS initiation_status,\r\n  initiate_procurements.initiated_by,\r\n  initiate_procurements.dateadded,\r\n  bidinvitations.isapproved AS bid_approved,\r\n  bidinvitations.date_approved AS date_bid_approved,\r\n  bidinvitations.dateadded AS date_bid_added,\r\n  bidinvitations.approvedby AS bid_approvedby,\r\n  bidinvitations.invitation_to_bid_date,  bidinvitations.bid_submission_deadline,\r\n  bidinvitations.id AS bid_invitation_id\r\nFROM procurement_plans\r\n  INNER JOIN procurement_plan_entries\r\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\r\n  INNER JOIN procurement_types\r\n    ON procurement_plan_entries.procurement_type = procurement_types.id\r\n  INNER JOIN procurement_methods\r\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\r\n  INNER JOIN funding_sources\r\n    ON procurement_plan_entries.funding_source = funding_sources.id\r\n  INNER JOIN pdes\r\n    ON procurement_plans.pde_id = pdes.pdeid\r\n  LEFT OUTER JOIN initiate_procurements\r\n    ON procurement_plan_entries.procurement_ref_no = initiate_procurements.procurement_ref_no\r\n  LEFT OUTER JOIN bidinvitations\r\n    ON procurement_plan_entries.procurement_ref_no = bidinvitations.procurement_ref_no \r\nWHERE _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-09 00:00:00'),
(629, 'active_procurement_list', 'Active Procurement List', '  SELECT  DISTINCT   \n  bidinvitations.procurement_ref_no,   \n  procurement_plan_entries.subject_of_procurement,     \n  procurement_plans.financial_year,   procurement_plans.title \n       \n  FROM bidinvitations         \n  INNER JOIN   procurement_plan_entries        \n  ON bidinvitations.procurement_id   =  procurement_plan_entries.id  \n  INNER JOIN procurement_plans     \n  ON  procurement_plan_entries.procurement_plan_id = procurement_plans.id      \n     \n\n  INNER JOIN pdes        \n  ON pdes.pdeid = procurement_plans.pde_id      \n  INNER JOIN users      \n  ON pdes.pdeid = users.pde \n  LEFT OUTER  JOIN  bestevaluatedbidder\n  ON bidinvitations.id =  bestevaluatedbidder.bidid\n WHERE bestevaluatedbidder.id IS NULL\n\n\n\n  _SEARCHSTRING_  _LIMITTEXT_\n', '0000-00-00 00:00:00'),
(630, 'archive_restorereceipts', 'UPDATE RESTORE RECEIPTS', 'UPDATE receipts SET isactive = ''_STATUS_''  where    receiptid = _ID_ ', '2015-02-12 00:00:00'),
(631, 'procurement_plan_reports', 'Procurement plan reports', 'SELECT\r\n  *\r\nFROM (SELECT\r\n    PP.dateadded AS plan_dateadded,\r\n    PP.isactive AS pp_active_status,\r\n    PP.financial_year,\r\n    PP.ID AS plan_id,\r\n    PPE_entries.numOfEntries,\r\n    COALESCE(PPE_value.estimatedValue, 0) AS estimatedValue,\r\n    pdes.pdename,\r\n    pdes.pdeid,\r\n    procurement_plan_phases.title AS approval_status\r\n  FROM procurement_plans PP\r\n    LEFT OUTER JOIN (SELECT\r\n        COUNT(*) AS numOfEntries,\r\n        procurement_plan_entries.procurement_plan_id\r\n      FROM procurement_plan_entries\r\n      WHERE procurement_plan_entries.isactive = "Y"\r\n      GROUP BY procurement_plan_entries.procurement_plan_id) PPE_entries\r\n      ON PP.ID = PPE_entries.procurement_plan_id\r\n    LEFT OUTER JOIN (SELECT\r\n        SUM(procurement_plan_entries.estimated_amount * exchange_rate) AS estimatedValue,\r\n        procurement_plan_entries.procurement_plan_id\r\n      FROM procurement_plan_entries\r\n      WHERE procurement_plan_entries.isactive = "Y"\r\n      GROUP BY procurement_plan_entries.procurement_plan_id) PPE_value\r\n      ON PP.ID = PPE_value.estimatedValue\r\n    RIGHT OUTER JOIN pdes\r\n      ON PP.pde_id = pdes.pdeid\r\n    LEFT OUTER JOIN procurement_plan_statuses\r\n      ON PP.ID = procurement_plan_statuses.pp_id\r\n    LEFT OUTER JOIN procurement_plan_phases\r\n      ON procurement_plan_statuses.status_id = procurement_plan_phases.ID\r\n  ) PS\r\n  WHERE _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-19 09:00:00'),
(632, 'invitation_to_bid_reports', 'invitation to bid reports', 'SELECT\r\n  PP.financial_year,\r\n  PP.id AS plan_id,\r\n  pdes.pdename,\r\n  pdes.pdeid,\r\n  BI.procurement_ref_no,\r\n  PPE.subject_of_procurement,\r\n  BI.invitation_to_bid_date,\r\n  BI.bid_submission_deadline,\r\n  PPE.estimated_amount,\r\n  PPE.exchange_rate,\r\n  C.contract_amount,\r\n  COALESCE(BR.numOfBids, 0) AS numOfBids,\r\n  DATEDIFF(BI.invitation_to_bid_date, PPE.bid_issue_date) AS days_delayed,\r\n  DATEDIFF(BI.bid_submission_deadline, BI.invitation_to_bid_date) AS bid_submission_duration,\r\n  PPE.proposal_submission_date_duration,\r\n  procurement_methods.title AS procurement_method_title,\r\n  procurement_methods.biddingperiod\r\nFROM procurement_plans PP\r\n  INNER JOIN pdes\r\n    ON PP.pde_id = pdes.pdeid\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON PPE.procurement_plan_id = PP.id\r\n  INNER JOIN bidinvitations BI\r\n    ON PPE.id = BI.procurement_id\r\n  LEFT OUTER JOIN contracts C\r\n    ON PPE.id = C.procurement_ref_id\r\n  LEFT OUTER JOIN (SELECT\r\n      COUNT(*) AS numOfBids,\r\n      receipts.bid_id\r\n    FROM receipts\r\n    WHERE receipts.isactive = ''Y''\r\n    GROUP BY receipts.bid_id) BR\r\n    ON BI.id = BR.bid_id\r\n  INNER JOIN procurement_methods\r\n    ON PPE.procurement_method = procurement_methods.id\r\n  WHERE _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-19 10:18:22'),
(633, 'update_user_profile', 'Update the user profile', 'UPDATE users SET firstname=''_FIRSTNAME_'', lastname=''_LASTNAME_'', middlename=''_MIDDLENAME_'', \r\naddressline1=''_ADDRESSLINE1_'',emailaddress=''_EMAILADDRESS_'', telephone=''_TELEPHONE_'', username=''_USERNAME_'', prefix=''_PREFIX_'', gender=''_GENDER_'' _UPDATECOND_ WHERE userid=''_EDITID_''', '2015-02-25 00:00:00'),
(634, 'late_procurement_reports', 'Procurement plan reports', 'SELECT\r\n  pdes.pdeid,\r\n  pdes.pdename,\r\n  pdes.abbreviation,\r\n  PP.financial_year,\r\n  BI.procurement_ref_no,\r\n  PPE.subject_of_procurement,\r\n  BI.invitation_to_bid_date,\r\n  PPE.bid_issue_date,\r\n  DATEDIFF(BI.invitation_to_bid_date, PPE.bid_issue_date) AS days_delayed\r\nFROM pdes\r\n  INNER JOIN procurement_plans PP\r\n    ON pdes.pdeid = PP.pde_id\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON PPE.procurement_plan_id = PP.id\r\n  LEFT JOIN bidinvitations BI\r\n    ON PPE.id= BI.procurement_id\r\n  WHERE _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-26 00:00:00'),
(635, 'best_evaluated_bidder_reports', 'Best evaluated bidder reports', 'SELECT\r\n  PP.financial_year,\r\n  pdes.pdename,\r\n  pdes.abbreviation,\r\n  BI.procurement_ref_no,\r\n  PPE.subject_of_procurement,\r\n  PPE.procurement_type,\r\n  BEB.currency,\r\n  BEB.contractprice,\r\n  BEB.exchange_rate,\r\n  BEB.dateadded AS beb_dateadded,\r\n  BI.id AS bidinvitations_id,\r\n  BEB.id AS beb_row_id,\r\n  BEB.beb_expiry_date,\r\n  PPE.estimated_amount,\r\n  PPE.currency AS estimated_amount_currency,\r\n  DATEDIFF(BEB.evaluation_commencement_date, BEB.final_evaluation_report_approval_date) AS actual_beb_evaluation_duration,\r\n  PPE.best_evaluated_bidder_date_duration,\r\n  R.providerid,\r\n  providers.providernames,\r\n  R.joint_venture,\r\n  R.readoutprice,\r\n  procurement_types.title AS procurement_type_title,\r\n  procurement_types.evaluation_time\r\nFROM bidinvitations BI\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON PPE.id = BI.procurement_id\r\n  INNER JOIN procurement_plans PP\r\n    ON PPE.procurement_plan_id = PP.id\r\n  INNER JOIN pdes\r\n    ON PP.pde_id = pdes.pdeid\r\n  INNER JOIN bestevaluatedbidder BEB\r\n    ON BI.id = BEB.bidid\r\n  INNER JOIN (SELECT\r\n      receipts.receiptid,\r\n      receipts.bid_id,\r\n      receipts.providerid,\r\n      receipts.details,\r\n      receipts.received_by,\r\n      receipts.datereceived,\r\n      receipts.approved,\r\n      receipts.nationality,\r\n      receipts.author,\r\n      receipts.dateadded,\r\n      receipts.beb,\r\n      receipts.reason,\r\n      receipts.isactive,\r\n      receipts.joint_venture,\r\n      receipts.readoutprice,\r\n      receipts.currence\r\n    FROM receipts\r\n    WHERE receipts.isactive = "Y" AND receipts.beb = "Y") R\r\n    ON R.bid_id = BEB.bidid\r\n  LEFT OUTER JOIN providers\r\n    ON R.providerid = providers.providerid\r\n  INNER JOIN procurement_types\r\n    ON PPE.procurement_type = procurement_types.id\r\n  WHERE _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-28 05:24:28'),
(636, 'delete_awarded_contract', 'Deletes the awarded contract', 'UPDATE contracts SET isactive = ''N'' WHERE id = ''_ID_''', '2015-02-28 00:00:00'),
(637, 'get_contract_by_id', 'Returns the contract id used for editing or deleting', 'SELECT * FROM contracts WHERE id=''_ID_''', '2015-03-02 00:00:00'),
(638, 'update_contract', 'Edit contract details', 'UPDATE contracts\r\nSET\r\n  procurement_ref_no = ''_PREFID_'',\r\n  provider_id = ''_PROVIDERID_'',\r\n  admin_review = ''_OPTIONSRADIOS1_'',\r\n  date_of_sg_approval = ''_SGAPPROVAL_'',\r\n  final_award_notice_date = ''_AWARDNOTICE_'',\r\n  commencement_date = ''_COMMENCEDATE_'',\r\n  completion_date = ''_DATECOMPLETION_'',\r\n  contract_amount = ''_AMOUNT_'',\r\n  amount_currency = ''_CURRENCY_'',\r\n  author = ''_AUTHOR_''\r\nWHERE id = ''_ID_''', '2015-03-02 00:00:00');
INSERT INTO `appqueries` (`id`, `querycode`, `description`, `query`, `dateentered`) VALUES
(639, 'get_record_beb', 'Returns procurement records with the best evaluated bidder', 'SELECT\r\n  procurement_plan_entries.procurement_ref_no,\r\n  procurement_plan_entries.funding_source AS funding_source_id,\r\n  procurement_plan_entries.pde_department,\r\n  procurement_plan_entries.procurement_method AS procurement_method_id,\r\n  procurement_plan_entries.procurement_type AS procurement_type_id,\r\n  procurement_plan_entries.subject_of_procurement,\r\n  procurement_plan_entries.estimated_amount,\r\n  procurement_plan_entries.currency,\r\n  bidinvitations.id,\r\n  providers.providernames,\r\n  receipts.bid_id\r\n  \r\nFROM procurement_plan_entries\r\n	INNER JOIN bidinvitations\r\n		ON procurement_plan_entries.procurement_ref_no = bidinvitations.procurement_ref_no\r\n	INNER JOIN receipts\r\n		ON receipts.bid_id = bidinvitations.id \r\n	INNER JOIN providers\r\n		ON providers.providerid = receipts.providerid\r\nWHERE receipts.beb = "Y" AND receipts.isactive = "Y"', '2015-03-02 00:00:00'),
(640, 'view_user_notifications', 'View User Notifications ', ' SELECT    \n  notifications.id,  \n  notifications.title, \n  notifications.body,   \n  notifications.level,  \n  notifications.dateadded, \n  IF(notifications_status.id >0,''OLD'',''NEW'') AS statuss \n  FROM notifications      \n  INNER JOIN notifications_recepients    \n  ON notifications.id = notifications_recepients.notification_id   \n  LEFT OUTER JOIN notifications_status    \n  ON notifications_recepients.notification_id = notifications_status.notification_id    \n  WHERE  _SEARCHSTRING_ _LIMITTEXT_', '2015-03-02 00:00:00'),
(641, 'notification_detail', 'fetch Notification Details', 'SELECT * FROM notifications WHERE id = _ID_', '2015-03-02 00:00:00'),
(643, 'notification_access', 'Notification Access', 'SELECT  \n  DISTINCT  \n  users.firstname,  \n  users.lastname, \n  users.pde,  \n  users.userid, \n  permissions.code ,\n  users.emailaddress\n  FROM permissions  \n  INNER JOIN groupaccess\n  ON permissions.id = groupaccess.permissionid \n  INNER JOIN roles   \n  ON roles.groupid = groupaccess.groupid \n  INNER JOIN users  \n  ON users.userid = roles.userid \n  WHERE _SEARCHSTRING_', '2015-03-03 00:00:00'),
(644, 'add_disposal_record', 'Add Disposal Record', 'INSERT INTO  \n  disposal_record\n  (disposal_plan,pde,  disposal_serial_no,  subject_of_disposal,  asset_location,amount,  \n  currence,strategic_asset,  date_of_approval,  method_of_disposal,  isactive,  author,\n  dateadded,  dateofaoapproval,quantity) \n  VALUES (''_DISPOSALPLAN_'',''_PDE_'',''_DISPOSALSERIALNO_'', \n  ''_SUBJECTOFDISPOSAL_'',''_ASSETLOCATION_'',''_AMOUNT_'', \n  ''_CURRENCE_'',''_STRATEGICASSET_'',''_DATEOFAPPROVAL_'',\n  ''_METHODOFDISPOSAL_'',''_ISACTIVE_'',''_AUTHOR_'',\n  ''_DATEADDED_'',''_DATEOFAOAPPROVAL_'',''_ASSETQUANTITY_'')', '2015-03-11 00:00:00'),
(645, 'fetch_disposal_records', 'Fetch Disposal Records', '  SELECT DISTINCT         disposal_plans.title as disp_plan,   disposal_plans.financial_year, \n    disposal_plans.id as disposalpln_id,\n    disposal_record.id,           disposal_record.disposal_serial_no,  \n    disposal_record.subject_of_disposal,      disposal_record.asset_location,   \n    disposal_record.amount,           disposal_record.currence,  \n    disposal_record.author,         disposal_record.dateadded,  \n    disposal_record.strategic_asset,  \n    disposal_record.date_of_approval,  \n    disposal_record.dateofaoapproval,    pdes.pdename,     \n    pdes.abbreviation,           pdes.code,           disposal_method.method ,    \n    disposal_record.strategic_asset,     disposal_record.date_of_approval,    \n    disposal_record.method_of_disposal, \n    disposal_record.dateofaoapproval,\n    disposal_record.quantity    \n    FROM disposal_record          INNER JOIN disposal_method       \n    ON disposal_method.id = disposal_record.method_of_disposal      \n    INNER JOIN disposal_plans       \n    on disposal_record.disposal_plan  = disposal_plans.id        \n    INNER JOIN users          ON disposal_record.pde = users.pde    \n    INNER JOIN pdes         \n    ON users.pde = pdes.pdeid      \n    WHERE _SEARCHSTRING_  _LIMITTEXT_', '2015-03-11 00:00:00'),
(646, 'add_disposal_bids', 'Add Disposal Bid', 'INSERT INTO disposal_bid_invitation\n  (disposal_record,disposal_ref_no,bid_document_issue_date,bid_opening_date,\n  	bid_duration,cc_approval_date,isactive,author,dateadded,date_of_approval_form28,date_of_initiation_form28)\n\n  VALUES (''_DISPOSALSERIALNO_'',''_DISPOSALREFNO_'',''_DOCUMENTISSUEDATE_'',''_BIDOPENINGDATE_'',     \n  ''_BIDDURATION_'',''_CCAPPROVALDATE_'',''_ISACTIVE_'',''_AUTHOR_'',''_DATEADDED_'',''_DATEOFAPPROVALFORM28_'',''_DATEOFINITIATIONFORM28_'')\n\n ', '2015-03-11 00:00:00'),
(647, 'view_disposal_bid_invitations', 'View Bid Invitations', 'SELECT DISTINCT   \n  disposal_record.disposal_serial_no,\n  disposal_bid_invitation.bid_document_issue_date, \n  disposal_bid_invitation.bid_opening_date, \n  disposal_bid_invitation.bid_duration, \n  disposal_record.subject_of_disposal,  \n  disposal_bid_invitation.isactive,   \n  disposal_bid_invitation.author,   \n  disposal_bid_invitation.bid_duration,\n  disposal_bid_invitation.date_of_approval_form28, \n  disposal_bid_invitation.date_of_initiation_form28, \n  disposal_bid_invitation.cc_approval_date,   \n  disposal_bid_invitation.dateadded,  \n  disposal_bid_invitation.disposal_ref_no,\n  disposal_bid_invitation.id    \n  FROM disposal_bid_invitation  \n  INNER JOIN disposal_record  ON disposal_bid_invitation.disposal_record = disposal_record.id  INNER JOIN users       ON  users.pde = disposal_record.pde        WHERE _SEARCHSTRING_ _LIMITTEXT_', '2015-03-11 00:00:00'),
(648, 'publish_bid_invitation', 'Publish a bid invitation', 'UPDATE bidinvitations \r\nSET\r\n isapproved =  ''Y''\r\n ,date_approved = NOW() \r\n ,approvedby = _APPROVEDBY_\r\n ,cc_approval_date = ''_CC_APPROVAL_DATE_''\r\nWHERE\r\n  id = _BIDINVITATION_ID_', '2015-03-12 00:00:00'),
(649, 'add_disposal_plan', 'Add Disposal Plan', 'insert into \n  \n  disposal_plans(pde_id,financial_year,title,author,description)\n  \n  values (''_PDE_'',''_FINANCIALYEAR_'',''_TITLE_'',''_AUTHOR_'',''_DETAILS_'')', '2015-03-16 00:00:00'),
(650, 'view_disposal_plans', 'View Disposal Plans', 'SELECT DISTINCT a.*,C.pdename\n  \n  FROM disposal_plans as a\n  INNER JOIN users as b\n  on a.pde_id = b.pde\n  LEFT OUTER  JOIN pdes C\n  ON a.pde_id = C.pdeid\n  where _SEARCHSTRING_ _LIMITTEXT_\n', '0000-00-00 00:00:00'),
(651, 'count_procurement_records', 'Return number of procurement entries', 'SELECT\r\n  COUNT(*) AS numOfPlannedProcurements\r\nFROM procurement_plans PP\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON PP.id = PPE.procurement_plan_id\r\nWHERE PPE.isactive = ''Y'' AND PP.isactive = ''Y'' AND _SEARCHSTRING_', '2015-03-16 00:00:00'),
(652, 'count_contracts_awarded', 'Returns number of contracts awarded', 'SELECT\r\n  COUNT(*) AS numOfBebs\r\nFROM bidinvitations BI\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON BI.procurement_id = PPE.id\r\n  INNER JOIN procurement_plans PP\r\n    ON PPE.procurement_plan_id = PP.id\r\n  INNER JOIN contracts C\r\n    ON C.procurement_ref_id = PPE.id\r\nWHERE BI.isactive = ''Y'' AND PPE.isactive = ''Y'' AND PP.isactive = ''Y'' AND C.isactive = ''Y'' AND _SEARCHSTRING_', '2015-03-16 00:00:00'),
(653, 'count_bebs_published', 'No. of BEBs published', 'SELECT\r\n  COUNT(*) AS numOfBebs\r\nFROM bestevaluatedbidder BEB\r\n  INNER JOIN bidinvitations BI\r\n    ON BEB.bidid = BI.id\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON BI.procurement_id = PPE.id\r\n  INNER JOIN procurement_plans PP\r\n    ON PPE.procurement_plan_id = PP.id\r\n  WHERE BI.isactive = ''Y'' AND PPE.isactive = ''Y'' AND PP.isactive = ''Y'' AND _SEARCHSTRING_', '2015-03-16 00:00:00'),
(654, 'count_ifbs_published', 'Returns number of IFBs published', 'SELECT\r\n  COUNT(*) AS numOfBids\r\nFROM bidinvitations BI\r\n  INNER JOIN procurement_plan_entries PPE\r\n    ON BI.procurement_id = PPE.id\r\n  INNER JOIN procurement_plans PP\r\n    ON PPE.procurement_plan_id = PP.id\r\nWHERE BI.isactive = ''Y'' AND BI.isapproved = ''Y'' AND BI.cc_approval_date IS NOT NULL AND PPE.isactive = ''Y'' AND _SEARCHSTRING_', '2015-03-16 00:00:00'),
(655, 'count_plans_submitted', 'Returns a count of procurement plans submitted', 'SELECT\r\n  COUNT(*)\r\nFROM procurement_plans PP\r\n  INNER JOIN (SELECT\r\n      COUNT(*) AS numOfEntries,\r\n      ppe.procurement_plan_id\r\n    FROM procurement_plan_entries ppe\r\n    WHERE ppe.isactive = ''Y''\r\n    GROUP BY ppe.procurement_plan_id\r\n    HAVING COUNT(*) > 0) plan_entries\r\n    ON PP.ID = plan_entries.procurement_plan_id WHERE _SEARCHSTRING_', '2015-03-16 00:00:00'),
(656, 'contract_details', 'Show specific contract details', 'SELECT \r\n  procurement_plans.title AS procurement_plan,\r\n  procurement_plan_entries.procurement_ref_no,\r\n  procurement_plan_entries.contract_award,\r\n  procurement_plan_entries.subject_of_procurement,\r\n  procurement_types.title AS procurement_type,\r\n  procurement_methods.title AS procurement_method,\r\n  funding_sources.title AS funding_source,\r\n  pdes.pdename,\r\n  contracts.commencement_date,\r\n  contracts.contract_amount,\r\n  contracts.completion_date,\r\n  contracts.amount_currency,\r\n  contracts.days_duration,\r\n  contracts.dateawarded\r\n  \r\n  FROM procurement_plans\r\n  INNER JOIN procurement_plan_entries\r\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\r\n  INNER JOIN procurement_types\r\n    ON procurement_plan_entries.procurement_type = procurement_types.id\r\n  INNER JOIN procurement_methods\r\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\r\n  INNER JOIN funding_sources\r\n    ON procurement_plan_entries.funding_source = funding_sources.id\r\n  INNER JOIN pdes\r\n    ON procurement_plans.pde_id = pdes.pdeid\r\n  LEFT OUTER JOIN initiate_procurements\r\n    ON procurement_plan_entries.procurement_ref_no = initiate_procurements.procurement_ref_no\r\n  LEFT OUTER JOIN bidinvitations\r\n    ON procurement_plan_entries.procurement_ref_no = bidinvitations.procurement_ref_no\r\n  LEFT OUTER JOIN contracts\r\n  	ON procurement_plan_entries.procurement_ref_no = contracts.procurement_ref_no WHERE contracts.id = ''_ID_'' AND contracts.isActive = ''Y''', '0000-00-00 00:00:00'),
(657, 'fetch_bid_responses', 'Fetch Bid Responses', 'SELECT DISTINCT \r\n  providers.providernames,\r\n  disposal_record.disposal_ref_no,\r\n  bid_response.nationality,\r\n  bid_response.readoutprice,\r\n  bid_response.datesubmitted,\r\n  bid_response.receivedby,\r\n  bid_response.dateadded\r\n  \r\nFROM bid_response\r\n  INNER JOIN providers\r\n    ON bid_response.provider_id = providers.providerid\r\n  INNER JOIN disposal_bid_invitation\r\n    ON bid_response.bid_invitation = disposal_bid_invitation.id\r\n  INNER JOIN disposal_record\r\n  ON disposal_bid_invitation.disposal_ref_no = disposal_record.id\r\n   INNER JOIN  users\r\n    ON  disposal_record.pde =  users.pde   WHERE _SEARCHSTRING_ _LIMITTEXT_', '2015-03-18 00:00:00'),
(658, 'update_bid_invitation', 'Update invitation for bid information', '\n\n  UPDATE bidinvitations \nSET\n  bid_openning_date = ''_BID_OPENNING_DATE_''\n ,cost_estimate = ''_COST_ESTIMATE_''\n ,invitation_to_bid_date = ''_INVITATION_TO_BID_DATE_''\n ,bid_receipt_address = ''_BID_RECEIPT_ADDRESS_''\n ,pre_bid_meeting_date = ''_PRE_BID_MEETING_DATE_''\n ,procurement_ref_no = ''_PROCUREMENT_REF_NO_''\n ,description_of_works = ''_DESCRIPTION_OF_WORKS_''\n ,bid_security_amount = ''_BID_SECURITY_AMOUNT_''\n ,bid_security_currency = ''_CURRENCY_OF_BIDSECURITY_''\n ,bid_documents_price = ''_BID_DOCUMENTS_PRICE_''\n ,bid_submission_deadline = ''_BID_SUBMISSION_DEADLINE_''\n ,bid_evaluation_to = ''_BID_EVALUATION_TO_''\n ,bid_evaluation_from = ''_BID_EVALUATION_FROM_''\n ,display_of_beb_notice = ''_DISPLAY_OF_BEB_NOTICE_''\n ,contract_award_date = ''_CONTRACT_AWARD_DATE_''\n ,documents_inspection_address = ''_DOCUMENTS_INSPECTION_ADDRESS_''\n ,documents_address_issue = ''_DOCUMENTS_ADDRESS_ISSUE_''\n ,bid_openning_address = ''_BID_OPENNING_ADDRESS_''\n ,vote_no= ''_VOTE_NO_''\n ,initiated_by= ''_INITIATED_BY_''\n ,date_initiated= ''_DATE_INITIATED_''\n ,bidvalidityperiod= ''_VALIDITYPERIOD_''\n ,bidvalidity= ''_VALIDITY_''\n ,quantity= ''_QUANTITYIFB_''\n \nWHERE\n  id = ''_INVITATION_ID_''', '2015-03-19 00:00:00'),
(659, 'update_addenda', 'Update addenda details', 'UPDATE addenda \r\nSET\r\n  refno = ''_REFNO_''\r\n ,title = ''_TITLE_''\r\n ,description = ''_DESCRIPTION_''\r\n _UPDATESTR_\r\nWHERE\r\n  id = ''_ID_''', '2015-03-20 00:00:00'),
(660, 'search_addenda', 'Returns list of addenda', 'SELECT\r\n  PP.financial_year,\r\n  PPE.procurement_ref_no,\r\n  PPE.subject_of_procurement,\r\n  A.refno,\r\n  A.id AS addenda_id,\r\n  A.bidid,\r\n  A.title,\r\n  A.fileurl,\r\n  A.author,\r\n  A.dateadded,\r\n  U.userid,\r\n  CONCAT(U.firstname, '' '', U.lastname) AS authorname\r\nFROM procurement_plan_entries PPE\r\n  INNER JOIN procurement_plans PP\r\n    ON PPE.procurement_plan_id = PP.id\r\n  CROSS JOIN addenda A\r\n  INNER JOIN bidinvitations BI\r\n    ON A.bidid = BI.id AND PPE.id = BI.procurement_id\r\n  LEFT OUTER JOIN users U\r\n    ON A.author = U.userid\r\n  WHERE A.isactive = ''Y'' AND PP.isactive = ''Y'' AND PPE.isactive = ''Y'' _SEARCHSTRING_\r\n  ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-03-20 00:00:00'),
(661, 'create_addenda', 'Create addenda', 'INSERT INTO addenda\r\n(\r\n  bidid\r\n ,refno\r\n ,title\r\n ,description\r\n ,fileurl\r\n ,author\r\n)\r\nVALUES\r\n(\r\n _BIDID_\r\n ,''_REFNO_''\r\n ,''_TITLE_''\r\n ,''_DESCRIPTION_''\r\n ,''_FILEURL_''\r\n ,''_AUTHOR_''\r\n)', '2015-03-20 00:00:00'),
(662, 'delete_receipt', 'Delete Receipt', 'DELETE FROM receipts where receiptid = _ID_ ', '2015-03-26 00:00:00'),
(663, 'view_bebs', 'View Best Evaluated Bidder', '\n  SELECT  DISTINCT   \n  bidinvitations.procurement_ref_no,   \n  procurement_plan_entries.subject_of_procurement,   \n  procurement_methods.title as procurementmethod ,       \n  procurement_plans.financial_year,   procurement_plans.title ,\n  bestevaluatedbidder.type_oem,\n  bestevaluatedbidder.ddate_octhe,\n  bestevaluatedbidder.num_orb,\n  bestevaluatedbidder.date_oce_r,\n  bestevaluatedbidder.nationality,\n  bestevaluatedbidder.contractprice,\n  bestevaluatedbidder.currency,  \n  bestevaluatedbidder.dateadded,\n  evaluation_methods.evaluation_method_name,\n  bestevaluatedbidder.ispublished,\n  bestevaluatedbidder.isreviewed,\n  bestevaluatedbidder.id,\n  bidinvitations.id as bid_id,\n  bestevaluatedbidder.seerialnumber,\n  bestevaluatedbidder.review_level,\n  IF(receipts.providerid > 0,receipts.providerid,joint_venture.providers) as providers\n\n  FROM procurement_plan_entries         \n  INNER JOIN bidinvitations         \n  ON procurement_plan_entries.id = bidinvitations.procurement_id  \n  INNER JOIN procurement_plans     \n  ON  procurement_plan_entries.procurement_plan_id = procurement_plans.id    \n  INNER JOIN procurement_methods \n  ON   procurement_plan_entries.procurement_method =  procurement_methods.id \n  INNER JOIN pdes        \n  ON pdes.pdeid = procurement_plans.pde_id      \n  INNER JOIN users      \n  ON pdes.pdeid = users.pde \n  INNER  JOIN  bestevaluatedbidder\n  ON bidinvitations.id =  bestevaluatedbidder.bidid\n  INNER JOIN receipts \n  ON receipts.receiptid = bestevaluatedbidder.pid   \n  LEFT JOIN joint_venture\n  ON receipts.joint_venture = joint_venture.jv\n  INNER  JOIN evaluation_methods \n  ON bestevaluatedbidder.type_oem =  evaluation_methods.evaluation_method_id\n\n  _SEARCHSTRING_ _LIMITTEXT_', '2015-03-27 00:00:00'),
(664, 'procurement_plans', 'search for procurement plans', 'SELECT\r\n  U.firstname,\r\n  U.lastname,\r\n  PP.financial_year,\r\n  PP.Title,\r\n  PP.dateadded,\r\n  pdes.pdename,\r\n  pdes.abbreviation,\r\n  PP.summarized_plan,\r\n  PP.ID AS plan_id,\r\n  COALESCE(entries.numOfEntries, 0) AS numOfEntries\r\nFROM procurement_plans PP\r\n  INNER JOIN users U\r\n    ON PP.author = U.USERID\r\n  INNER JOIN pdes\r\n    ON PP.pde_id = pdes.pdeid\r\n  LEFT JOIN (SELECT\r\n      COUNT(*) AS numOfEntries,\r\n      PPE.procurement_plan_id\r\n    FROM procurement_plan_entries PPE\r\n    WHERE PPE.isactive = "Y"\r\n    GROUP BY PPE.procurement_plan_id) entries\r\n    ON PP.ID = entries.procurement_plan_id\r\n  WHERE PP.isactive="Y" _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-01 00:00:00'),
(665, 'procurement_entries', 'procurement entry list', 'SELECT\n  PPE.procurement_plan_id,\n  PP.financial_year,\n  PP.pde_id,\n  PPE.subject_of_procurement,\n  PT.title AS procurement_type,\n  pdes.pdename,\n  PM.title AS procurement_method,\n  C.title AS currency,\n  CONCAT(U.firstname, '' '', U.lastname) AS authorname,\n  PPE.id AS entryid,\n  funding_sources.title AS funding_source,\n  PPE.estimated_amount,\n  PPE.dateadded,\n  PPE.procurement_ref_no,\n  PPE.quantity,\n  C.abbr AS currency_abbr\nFROM procurement_plan_entries PPE\n  INNER JOIN procurement_plans PP\n    ON PPE.procurement_plan_id = PP.id\n  LEFT OUTER JOIN users U\n    ON PPE.author = U.userid\n  INNER JOIN pdes\n    ON PP.pde_id = pdes.pdeid\n  INNER JOIN procurement_types PT\n    ON PPE.procurement_type = PT.id\n  LEFT OUTER JOIN procurement_methods PM\n    ON PPE.procurement_method = PM.id\n  LEFT OUTER JOIN currencies C\n    ON PPE.currency = C.id\n  LEFT OUTER JOIN funding_sources\n    ON PPE.funding_source = funding_sources.id\n WHERE PPE.isactive = "Y" _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-02-01 00:00:00'),
(666, 'fetchbebs', 'Fetch Bebs', ' SELECT DISTINCT\n    receipts.receiptid,       \n    bidinvitations.procurement_ref_no,   \n    procurement_plan_entries.subject_of_procurement,  \n    if(receipts.providerid > 0, receipts.providerid , joint_venture.providers ) as providernames, \n    receipts.datereceived,   \n    receipts.received_by,   \n    receipts.nationality,     \n    bestevaluatedbidder.dateadded,    \n    receipts.beb , \n    receipts.readoutprice, \n	  bidinvitations.id as bidid,\n    bestevaluatedbidder.contractprice,\n    bestevaluatedbidder.currency,\n    bestevaluatedbidder.seerialnumber,\n    bestevaluatedbidder.review_level,\n    if(joint_venture.provider_lead > 0,joint_venture.provider_lead,0) as providerlead,\n    bestevaluatedbidder.isreviewed,    \n    b.pdename\n  \n    FROM receipts     \n    INNER JOIN bidinvitations      \n    ON receipts.bid_id = bidinvitations.id      \n    INNER JOIN procurement_plan_entries        \n    ON bidinvitations.procurement_id = procurement_plan_entries.id    \n    INNER JOIN procurement_plans a       ON procurement_plan_entries.procurement_plan_id = a.id   \n    INNER JOIN pdes b     ON a.pde_id = b.pdeid    \n    INNER JOIN users  c   ON c.pde = b.pdeid  \n\n    INNER JOIN procurement_types  E\n    ON \n    procurement_plan_entries.procurement_type =  E.id\n\n    INNER JOIN procurement_methods F\n    ON\n    procurement_plan_entries.procurement_method = F.id\n\n    INNER JOIN funding_sources I\n    ON\n    procurement_plan_entries.funding_source = I.id\n\n    INNER JOIN  procurement_plans \n    ON procurement_plan_entries.procurement_plan_id = procurement_plans.id\n    INNER JOIN  bestevaluatedbidder \n    ON receipts.receiptid = bestevaluatedbidder.pid\n    INNER JOIN pdes \n    ON procurement_plans.pde_id = pdes.pdeid\n    LEFT OUTER JOIN  joint_venture ON joint_venture.jv = receipts.joint_venture\n    WHERE 1 and 1     \n    _SEARCHSTRING_  _LIMITTEXT_   \n   ', '2015-04-07 00:00:00'),
(667, 'contract_award_reports', 'Contract award reports', 'SELECT\r\n  C.dateawarded,\r\n  C.id AS contract_id,\r\n  C.completion_date,\r\n  C.provider_id,\r\n  C.contract_amount,\r\n  PPE.subject_of_procurement,\r\n  PPE.procurement_ref_no,\r\n  pdes.pdeid,\r\n  PP.financial_year,\r\n  PPE.estimated_amount,\r\n  PPE.currency,\r\n  pdes.pdename\r\nFROM procurement_plan_entries PPE\r\n  INNER JOIN contracts C\r\n    ON PPE.procurement_ref_no = C.procurement_ref_no\r\n  INNER JOIN procurement_plans PP\r\n    ON PPE.procurement_plan_id = PP.id\r\n  INNER JOIN pdes\r\n    ON PP.pde_id = pdes.pdeid\r\n  WHERE _SEARCHSTRING_ ORDER By _ORDERBY_ _LIMITTEXT_', '2015-04-07 00:00:00'),
(668, 'search_procurement_by_beb', 'Search Procurement Ref No, using Best Evaluated Bidder ID', '\nSELECT bidinvitations.*,bidinvitations.procurement_ref_no as procurement_ref_no  FROM bidinvitations \n  INNER  JOIN receipts\n  ON bidinvitations.id = receipts.bid_id\n  INNER JOIN bestevaluatedbidder\n ON bestevaluatedbidder.pid = receipts.receiptid\n  INNER JOIN procurement_plan_entries \n  ON bidinvitations.procurement_id =  procurement_plan_entries.id\n_SEARCHSTRING_ _LIMITTEXT_', '2015-04-09 00:00:00'),
(669, 'activ_procurement_list', 'Active Procurements 2', '      SELECT  DISTINCT   \n  bidinvitations.procurement_ref_no,   \n  bidinvitations.procurement_id,\nprocurement_plan_entries.subject_of_procurement,     \n  procurement_plans.financial_year,   procurement_plans.title    \n  FROM procurement_plan_entries         \n  INNER JOIN bidinvitations         \n  ON procurement_plan_entries.id = bidinvitations.procurement_id \n  INNER JOIN procurement_plans     \n  ON  procurement_plan_entries.procurement_plan_id = procurement_plans.id      \n     \n  INNER JOIN pdes        \n  ON pdes.pdeid = procurement_plans.pde_id      \n  INNER JOIN users      \n  ON pdes.pdeid = users.pde \n  LEFT OUTER  JOIN  bestevaluatedbidder\n  ON bidinvitations.id =  bestevaluatedbidder.bidid\n WHERE \n  _SEARCHSTRING_  _LIMITTEXT_\n', '2015-04-09 00:00:00'),
(670, 'updatebeb', 'Update BEB Proccess', 'UPDATE \r\nbestevaluatedbidder \r\nSET pid=''_PID_'',bidid=''_BIDID_'',type_oem=''_EVALUATIONMETHOD_'',ddate_octhe=''_DATEOFCOMMENCEMENT_'',\r\n num_orb=''_NUMBIDS_'',date_oce_r=''_DOBEVALUATION_'',\r\n date_oaoterbt_cc=''DATECC'',bebid=''_BEBNAME_'',\r\n nationality=''_BEBNATIONALITY_'',contractprice=''_CONTRACTPRICE_'',currency=''_CURRENCY_'',ispublished=''_ISPUBLISHED_''\r\n WHERE id = ''_BEBID_''', '2015-04-09 00:00:00'),
(671, 'uncontracted_procurements', 'Returns procurement ref nos with no contracts', 'SELECT\n  PPE.id,\n  BI.procurement_ref_no\nFROM contracts C\n  RIGHT OUTER JOIN procurement_plan_entries PPE\n    ON C.procurement_ref_id = PPE.id\n  INNER JOIN procurement_plans PP\n    ON PPE.procurement_plan_id = PP.id\n  INNER JOIN bidinvitations BI\n    ON PPE.id = BI.procurement_id\n  INNER JOIN (SELECT\n      receipts.bid_id\n                FROM receipts\n    WHERE receipts.beb = "Y" AND receipts.isactive = "Y"\n    GROUP BY bid_id) R\n    ON BI.id = R.bid_id\n  WHERE PPE.isactive = ''Y'' AND C.id IS NULL _SEARCHSTRING_ ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-04-10 00:00:00'),
(672, 'get_IFB_BEB', 'Get IFB BEB', 'SELECT\r\n  IF(R.providerid>0, P.providernames, R.joint_venture) AS providername,\r\n  R.bid_id,\r\n  BI.pde_id,\r\n  BI.procurement_id,\r\n  P.providerid,\r\n  R.joint_venture\r\nFROM bidinvitations BI\r\n  INNER JOIN receipts R\r\n    ON BI.id = R.bid_id\r\n	LEFT JOIN providers P ON R.providerid = P.providerid\r\n  WHERE R.beb = "Y" _SEARCHSTRING_', '2015-04-13 00:00:00'),
(673, 'fetch_lots', 'FETCH LOTS', 'SELECT a.* FROM lots a\n  INNER JOIN bidinvitations  b ON a.bid_id  =  b.id \n   INNER JOIN procurement_plan_entries     \n  ON  b.procurement_id = procurement_plan_entries.id      \n\n  WHERE \n  _SEARCHSTRING_ _LIMITTEXT_ _ORDERBY_\n  \n\n  \n', '2015-04-15 00:00:00'),
(674, 'complete_contract', 'Complete a contract', 'UPDATE contracts \r\nSET\r\n advance_payment =  ''_ADVANCEPAYMENT_''\r\n ,advance_payment_date = ''_ADVANCEPAYMENTDATE_''\r\n ,final_contract_value = ''_FINALCONTRACTVALUE_''\r\n ,final_contract_value_currency = ''_FINALCONTRACTVALUECURRENCY_''\r\n ,total_actual_payments = ''_TOTALACTUALPAYMENTS_''\r\n ,total_actual_payments_currency = ''_TOTALACTUALPAYMENTSCURRENCY_''\r\n ,actual_completion_date = ''_ACTUALCOMPLETIONDATE_''\r\n ,performance_rating = ''_PERFORMANCERATING_''\r\n ,commencement_letter = ''_COMMENCEMENTLETTER_''\r\n ,commencement_letter_ref_no = ''_COMMENCEMENTLETTERREFNO_''\r\n ,implementation_plan = ''_IMPLEMENTATIONPLAN_''\r\n ,contract_progress_report = ''_CONTRACTPROGRESSREPORT_''\r\n ,contract_manager = ''_CONTRACTMANAGER_''\r\n ,contract_management_report = ''_CONTRACTMANAGEMENTREPORT_''\r\n ,completion_author = ''_COMPLETIONAUTHOR_''\r\nWHERE\r\n  id = ''_ID_''', '2015-04-16 00:00:00'),
(675, 'contract_price_detail', 'Contract price details', 'SELECT\r\n  C.title,\r\n  CP.contract_id,\r\n  CP.amount,\r\n  CP.xrate,\r\n  CP.currency_id\r\nFROM contract_prices CP\r\n  INNER JOIN currencies C\r\n    ON CP.currency_id = C.id WHERE CP.isactive = "Y" _SEARCHSTRING_', '2015-04-17 00:00:00'),
(676, 'fetch_disposal_methods', 'Fetch Disposal Methods', '\r\nSELECT * FROM  disposal_method  where 1 = 1\r\n  _SEARCHSTRING_   _LIMITTEXT_\r\n', '2015-04-23 00:00:00'),
(677, 'active_disposal_records', 'Active Disposal Records', '\n       SELECT DISTINCT \n        \n        disposal_plans.title as disp_plan, \n        disposal_record.id,  \n        disposal_record.disposal_serial_no,     \n        disposal_record.subject_of_disposal,  \n        disposal_record.asset_location, \n        disposal_record.amount,    \n        disposal_record.currence,  \n        disposal_record.author,  \n        disposal_record.dateadded,\n        pdes.pdename,    \n        pdes.abbreviation,   \n        pdes.code\n        FROM disposal_record \n        INNER JOIN disposal_plans \n        on disposal_record.disposal_plan  = disposal_plans.id   \n        INNER JOIN users   \n        ON disposal_record.pde = users.pde  \n        INNER JOIN pdes   \n        ON users.pde = pdes.pdeid   \n        INNER JOIN  disposal_method ON  \n        disposal_record.method_of_disposal = disposal_method.id \n        WHERE  1=1  _SEARCHSTRING_  _LIMITTEXT_', '2015-04-23 00:00:00'),
(678, 'active_disposal_reference_numbers', 'Active Disposal Reference Numbers', 'SELECT DISTINCT     disposal_bid_invitation.disposal_record,    disposal_bid_invitation.disposal_ref_no,    disposal_bid_invitation.bid_document_issue_date,   disposal_bid_invitation.bid_opening_date,   disposal_bid_invitation.bid_duration,    disposal_bid_invitation.cc_approval_date,   disposal_record.disposal_plan,    disposal_record.disposal_serial_no,    disposal_record.subject_of_disposal,     disposal_record.amount,     disposal_plans.financial_year,     disposal_plans.title,    pdes.pdename,   pdes.pdeid ,   disposal_record.asset_location,   disposal_record.amount,    disposal_record.currence,   disposal_method.method    FROM disposal_bid_invitation    INNER JOIN disposal_record   ON disposal_bid_invitation.disposal_record = disposal_record.id   INNER JOIN disposal_plans   ON   disposal_record.disposal_plan = disposal_plans.id    INNER JOIN disposal_method   ON disposal_record.method_of_disposal = disposal_method.id   INNER JOIN users    ON users.pde = disposal_plans.pde_id    INNER JOIN pdes   ON users.pde = pdes.pdeid       _SEARCHSTRING_ _LIMITTEXT_  ', '2015-04-25 00:00:00'),
(679, 'fetch_disposal_receipts', 'Fetch Disposal Receipts', '  SELECT          disposal_receipts.receiptid,        disposal_bid_invitation.disposal_ref_no,           disposal_record.subject_of_disposal,           if(disposal_receipts.providerid	 > 0,disposal_receipts.providerid, joint_venture.providers) as providernames ,       disposal_receipts.datereceived,      disposal_receipts.received_by,        disposal_receipts.nationality,        disposal_receipts.dateadded,         disposal_receipts.beb ,          disposal_receipts.readoutprice ,         disposal_bid_invitation.id as bidid,     joint_venture.provider_lead     FROM disposal_receipts       INNER JOIN       disposal_bid_invitation on disposal_receipts.bid_id = disposal_bid_invitation.id        INNER JOIN      disposal_record ON disposal_bid_invitation.disposal_record = disposal_record.id       LEFT OUTER JOIN joint_venture ON        disposal_receipts.joint_venture = joint_venture.jv    _SEARCHSTRING_       ', '2015-04-25 00:00:00'),
(680, 'fetch_disposal_evaluation_methods', 'fetch Disposal evaluation methods', 'SELECT * FROM `disposal_evaluation_methods`   where _SEARCHSTRING_\r\n', '2015-04-27 00:00:00'),
(681, 'insertbebdisposal', 'Insert BEB diposal BEB', 'INSERT INTO \r\nbestevaluatedbidder_disposal \r\n(pid,bidid,type_oem,ddate_octhe,num_orb,date_oce_r,date_oaoterbt_cc,bebid,\r\n nationality,contractprice,currency,ispublished,num_orb_local)\r\nVALUES\r\n(''_PID_'',''_BIDID_'',''_EVALUATIONMETHOD_'',''_DATEOFCOMMENCEMENT_'',''_NUMBIDS_'',''_DOBEVALUATION_'',''DATECC'',''_BEBNAME_'',\r\n ''_BEBNATIONALITY_'',''_CONTRACTPRICE_'',''_CURRENCY_'',''_ISPUBLISHED_'',''_NUMORBLOCAL_'')  ', '2015-04-27 00:00:00'),
(682, 'checkfinancial_year', 'Check for FInancial Years', 'SELECT financial_year  FROM disposal_plans  WHERE disposal_plans.pde_id = ''_PDE_''  AND  financial_year like ''%_YEAR_%''  _SEARCHSTTRING_', '2015-04-29 00:00:00'),
(683, 'view_disposal_bebs', 'View Disposal Bebs', '  SELECT   DISTINCT        \n    disposal_receipts.receiptid,     \n    disposal_bid_invitation.disposal_ref_no,   \n    disposal_record.subject_of_disposal,       \n    if(disposal_receipts.providerid	 > 0,disposal_receipts.providerid, joint_venture.providers) as providernames , \n    disposal_receipts.datereceived,      disposal_receipts.received_by,      \n    disposal_receipts.nationality,      disposal_receipts.dateadded,        \n    disposal_receipts.beb ,    \n    disposal_receipts.readoutprice ,     \n    disposal_bid_invitation.id as bidid,    \n    joint_venture.provider_lead ,         pdes.pdename,   \n    bestevaluatedbidder_disposal.ispublished,   \n    bestevaluatedbidder_disposal.isreviewed ,    \n    bestevaluatedbidder_disposal.contractprice as contractprice,   \n    bestevaluatedbidder_disposal.currency as contractcurrency , \n    bestevaluatedbidder_disposal.dobccaward   \n    FROM disposal_receipts      \n    INNER JOIN     \n    bestevaluatedbidder_disposal    \n    ON      disposal_receipts.receiptid =  bestevaluatedbidder_disposal.pid   \n    INNER JOIN        disposal_bid_invitation     \n    on disposal_receipts.bid_id = disposal_bid_invitation.id       \n    INNER JOIN      disposal_record     \n    ON     disposal_bid_invitation.disposal_record = disposal_record.id  \n    LEFT OUTER JOIN joint_venture ON      \n    disposal_receipts.joint_venture = joint_venture.jv     \n    INNER JOIN disposal_plans    \n    ON disposal_record.disposal_plan = disposal_plans.id    \n    INNER JOIN users        ON users.pde = disposal_plans.pde_id    \n    INNER JOIN pdes        ON users.pde = pdes.pdeid      \n    where _SEARCHSTRING_ _LIMITTEXT_      ', '2015-04-29 00:00:00'),
(684, 'update_disposal', 'Update Disposal ', 'UPDATE   disposal_record  \n  SET disposal_plan =''_DISPOSALPLAN_'',\n  pde =''_PDE_'',  \n  disposal_serial_no = ''_DISPOSALSERIALNO_'',   \n  subject_of_disposal=''_SUBJECTOFDISPOSAL_'', \n  asset_location=''_ASSETLOCATION_'',\n  amount=''_AMOUNT_'', \n  currence=''_CURRENCE_'', \n  strategic_asset=''_STRATEGICASSET_'', \n  date_of_approval=''_DATEOFAPPROVAL_'',\n  method_of_disposal=''_METHODOFDISPOSAL_'',  \n  isactive=''_ISACTIVE_'', \n  author=''_AUTHOR_'',  \n   dateadded=''_DATEADDED_'',  \n  dateofaoapproval=''_DATEOFAOAPPROVAL_'' ,\n  quantity = ''_ASSETQUANTITY_''\n  \n  WHERE disposal_record.id = ''_ID_''   ', '2015-05-04 00:00:00'),
(685, 'archive_diposal_plans_records', 'Archive Disposal Record', ' UPDATE disposal_record SET isactive=''_ISACTIVE_'' WHERE id = ''_ID_''', '2015-05-04 00:00:00'),
(686, 'update_disposal_bids', 'Update Disposal Bids', 'UPDATE  disposal_bid_invitation    SET disposal_ref_no=''_DISPOSALREFNO_'',bid_document_issue_date=''_DOCUMENTISSUEDATE_'',bid_opening_date=''_BIDOPENINGDATE_'',  	bid_duration=''_BIDDURATION_'',cc_approval_date=''_CCAPPROVALDATE_'',isactive=''_ISACTIVE_'',author=''_AUTHOR_'',dateadded=''_DATEADDED_'',date_of_approval_form28=''_DATEOFAPPROVALFORM28_'',date_of_initiation_form28=''_DATEOFINITIATIONFORM28_''  WHERE id =''_ID_''   ', '2015-05-04 00:00:00'),
(687, 'create_procurementref_no', 'Create Procurement Ref NO ', 'SELECT \r\n  A.id,\r\n concat(D.abbreviation,''/'',B.code,''/'',C.financial_year,''/'') as concateddate\r\n  \r\n  FROM procurement_plan_entries A\r\n  INNER JOIN  procurement_types B\r\n  ON A.procurement_type = B.id\r\n  INNER JOIN\r\n  procurement_plans C\r\n  ON A.procurement_plan_id = C.id\r\n  INNER JOIN pdes D\r\n  ON C.pde_id = D.pdeid  \r\n  WHERE A.id = ''_ID_''\r\n\r\n \r\n', '2015-05-07 00:00:00'),
(688, 'view_disposal_contracts', 'View Disposal Contracts', 'SELECT B.disposal_serial_no,C.providernames,A.contractamount,A.datesigned,A.dateadded FROM disposal_contract A \r\n  INNER JOIN disposal_record B\r\nON A.disposalrecord = B.id\r\n  INNER JOIN providers C\r\nON A.beneficiary = C.providerid\r\n  WHERE 1 AND 1 _SEARCHSTRING_ LIMITTEXT  _ORDERBY_\r\n', '2015-05-12 00:00:00'),
(689, 'manage_disposal_contracts', 'manage_disposal_contracts', 'SELECT B.disposal_serial_no,C.providernames,A.contractamount,A.currency,A.datesigned,A.dateadded FROM disposal_contract A \n  INNER JOIN disposal_record B\nON A.disposalrecord = B.id\n  INNER JOIN providers C\nON A.beneficiary = C.providerid\n  INNER JOIN disposal_plans E\n  ON B.disposal_plan = E.id\n   WHERE 1 AND 1 _SEARCHSTRING_ _LIMITTEXT_  \n\n', '2015-05-12 00:00:00'),
(690, 'numbids', 'Number of Bids', 'SELECT\nCOUNT(bidinvitations.id) AS numbids\nFROM procurement_plans\n  INNER JOIN procurement_plan_entries\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\n  INNER JOIN procurement_types\n    ON procurement_plan_entries.procurement_type = procurement_types.id\n  INNER JOIN procurement_methods\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\n  INNER JOIN funding_sources\n    ON procurement_plan_entries.funding_source = funding_sources.id\n  INNER JOIN pdes\n    ON procurement_plans.pde_id = pdes.pdeid\n  INNER JOIN bidinvitations\n    ON bidinvitations.procurement_id = procurement_plan_entries.id\n  LEFT OUTER JOIN users\n    ON bidinvitations.approvedby = users.userid\n  LEFT OUTER JOIN (SELECT\n      COUNT(*) AS numOfAddenda,\n      addenda.bidid\n    FROM addenda\n    WHERE addenda.isactive = "Y"\n    GROUP BY addenda.bidid) addenda\n    ON bidinvitations.id = addenda.bidid\n  LEFT OUTER JOIN currencies\n    ON procurement_plan_entries.currency = currencies.id\n  LEFT OUTER JOIN currencies currencies_B\n    ON bidinvitations.bid_security_currency = currencies_B.id\n\n\n\n\n  WHERE _SEARCHSTRING_   _LIMITTEXT_', '2015-05-12 00:00:00'),
(691, 'insert_notification', 'Insert Notification', '  INSERT IGNORE  INTO notifications(title,body,isactive,level)    VALUES(''_TITLE_'',''_BODY_'',''_ISACTIVE_'',''_LEVEL_'')', '2015-05-14 00:00:00'),
(692, 'insert_notifications_recipients', 'Insert Notifications Recepients', 'INSERT IGNORE INTO notifications_recepients(recepient_id,notification_id,isactive)  VALUES(''_RECEPIENTID_'',''_NOTIFICATIONID_'',''_ISACTIVE_'')', '2015-05-14 00:00:00'),
(693, 'old_notifications', 'Old Notifications', ' SELECT  COUNT(*)  \r\n    AS numOfNotifications   FROM notifications   \r\n    INNER JOIN notifications_recepients   ON notifications.id = notifications_recepients.notification_id\r\n    WHERE  notifications_recepients.notification_id  IN(SELECT notification_id FROM notifications_status _SEARCHSTRING_) _SEARCHSTRING2_  \r\n', '2015-05-14 00:00:00'),
(694, 'sample_new_notifications', 'Sample New Notifications', '  SELECT     notifications.id,    notifications.title,    notifications.body,    notifications.level,    notifications.dateadded  FROM notifications   INNER JOIN notifications_recepients   ON notifications.id = notifications_recepients.notification_id WHERE  notifications_recepients.notification_id NOT IN(SELECT notification_id FROM notifications_status _SEARCHSTRING_) _SEARCHSTRING2_  ', '2015-05-14 00:00:00'),
(695, 'insert_notification_status', 'Insert Notification Status', '  INSERT IGNORE  INTO    notifications_status  \n(notification_id,userid,status)      VALUES(''_NOTIFICATION_'',''_USERID_'',''_STATUS_'')', '2015-05-14 00:00:00'),
(696, 'weekly_IFB_report', 'Weekly IFB Report', 'SELECT  procurement_plans.financial_year, bidinvitations.procurement_ref_no, procurement_plan_entries.funding_source AS funding_source_id,    procurement_plan_entries.pde_department,  procurement_plans.dateadded,  procurement_plan_entries.procurement_method AS procurement_method_id, procurement_plan_entries.procurement_type AS procurement_type_id, procurement_plan_entries.subject_of_procurement,  procurement_plan_entries.estimated_amount,  procurement_plan_entries.currency,  procurement_types.title AS procurement_type,  procurement_methods.title AS procurement_method,  funding_sources.title AS funding_source,  pdes.pdename,  bidinvitations.id AS bidinvitation_id,  bidinvitations.bid_openning_date,  bidinvitations.bid_submission_deadline AS submissiondate,  bidinvitations.pde_id,  bidinvitations.cost_estimate,  bidinvitations.invitation_to_bid_date, bidinvitations.pre_bid_meeting_date,  bidinvitations.description_of_works,  bidinvitations.bid_security_amount,  bidinvitations.bid_security_currency,  bidinvitations.bid_documents_price,  bidinvitations.author AS bid_author,  bidinvitations.isapproved AS bid_approved,    bidinvitations.dateadded AS bid_dateadded,  bidinvitations.approvedby AS bid_approvedby,  bidinvitations.isactive AS bid_isactive, bidinvitations.date_approved AS bid_date_approved,  bidinvitations.approval_comments AS bid_approval_comments,  bidinvitations.bid_submission_deadline,  bidinvitations.bid_evaluation_to,  bidinvitations.bid_evaluation_from,  bidinvitations.display_of_beb_notice,  bidinvitations.contract_award_date,  CONCAT(users.firstname, '' '', users.lastname) AS approver_fullname, COALESCE(addenda.numOfAddenda, 0) AS numOfAddenda,  currencies.title AS cost_estimate_currency,  currencies_B.title AS bid_security_currency_title    FROM    procurement_plans  INNER JOIN procurement_plan_entries   ON procurement_plans.id = procurement_plan_entries.procurement_plan_id  INNER JOIN procurement_types    ON procurement_plan_entries.procurement_type = procurement_types.id  INNER JOIN procurement_methods    ON procurement_plan_entries.procurement_method = procurement_methods.id    INNER JOIN funding_sources    ON procurement_plan_entries.funding_source = funding_sources.id  INNER JOIN pdes    ON procurement_plans.pde_id = pdes.pdeid    INNER JOIN bidinvitations    ON bidinvitations.procurement_id = procurement_plan_entries.id  LEFT OUTER JOIN users   ON bidinvitations.approvedby = users.userid  LEFT OUTER JOIN (SELECT      COUNT(*) AS numOfAddenda,      addenda.bidid    FROM addenda    WHERE addenda.isactive = "Y"    GROUP BY addenda.bidid)   addenda    ON bidinvitations.id = addenda.bidid  LEFT OUTER JOIN currencies    ON procurement_plan_entries.currency = currencies.id  LEFT OUTER JOIN currencies currencies_B   ON bidinvitations.bid_security_currency = currencies_B.id  WHERE   ((bidinvitations.bid_submission_deadline <  DATE_ADD(NOW(),INTERVAL 1 WEEK)) AND ( bidinvitations.bid_submission_deadline >= NOW()))  _SEARCHSTRING_  _ORDERBY_  ', '2015-05-14 00:00:00'),
