(703, 'procurement_search_list', 'Procurement Search List', '\n\nSELECT  DISTINCT\n  procurement_plan_entries.quantity,\n  procurement_plan_entries.subject_of_procurement,\n  procurement_types.title as procurementtype,\n  procurement_methods.title as procurementmethod,\n  funding_sources.title as funding_source,\n  procurement_plan_entries.estimated_amount,\n  currencies.title as currency,\n  procurement_plans.financial_year,\n  pdes.pdename\n  FROM \n  procurement_plan_entries\n\n  INNER JOIN procurement_types\n  ON procurement_plan_entries.procurement_type =  procurement_types.id\n  INNER JOIN  procurement_methods\n  ON procurement_plan_entries.procurement_method =  procurement_methods.id\n  LEFT OUTER  JOIN funding_sources\n  ON procurement_plan_entries.funding_source = funding_sources.id\n  LEFT OUTER JOIN currencies \n  ON procurement_plan_entries.currency =  currencies.id\n  INNER JOIN\n  procurement_plans \n  ON procurement_plan_entries.procurement_plan_id = procurement_plans.id\n  INNER JOIN \n  pdes ON\n  procurement_plans.pde_id = pdes.pdeid\n  WHERE  _SEARCHSTRING_', '2015-05-21 00:00:00'),
(704, 'archive_diposal_plans', 'Archive Disposal Plans', 'UPDATE disposal_plans SET isactive =''_ISACTIVE_'' WHERE id = ''_ID_''', '2015-06-02 00:00:00'),
(705, 'update_disposal_plan', 'UPDATE DISPOSAL PLAN', 'UPDATE \n  \n  disposal_plans SET\n  pde_id =''_PDE_'',\n  financial_year =''_FINANCIALYEAR_'', \n  author = ''_AUTHOR_''\n  WHERE id = ''_ID_''', '2015-06-02 00:00:00'),
(706, 'fetched_disposal_plans', 'FETCHED DISPOSAL PLANS', '  SELECT DISTINCT     \r\n    disposal_plans.title as disp_plan, \r\n    disposal_plans.financial_year, \r\n    disposal_plans.id as disposalpln_id,   \r\n    pdes.pdename,     \r\n    pdes.abbreviation,          \r\n    pdes.code      \r\n     \r\n    FROM disposal_plans              \r\n    INNER JOIN users         \r\n    ON disposal_plans.pde_id = users.pde\r\n    \r\n        \r\n    INNER JOIN pdes         \r\n    ON users.pde = pdes.pdeid      \r\n    WHERE _SEARCHSTRING_  _LIMITTEXT_', '2015-06-02 00:00:00'),
(708, 'ProcurementPlanDetails', 'Procurement Plan Details', 'SELECT DISTINCT \n  procurement_plans.financial_year,\n  procurement_plans.pde_id,\n  procurement_plans.id AS procurement_plan_id,\n  procurement_plan_entries.id AS procurement_id,\n   \n  procurement_plan_entries.funding_source AS funding_source_id,\n  procurement_plan_entries.pde_department,\n  procurement_plans.dateadded,\n  procurement_plan_entries.procurement_method AS procurement_method_id,\n  procurement_plan_entries.procurement_type AS procurement_type_id,\n  procurement_plan_entries.subject_of_procurement,\n  procurement_plan_entries.estimated_amount,\n  procurement_plan_entries.quantity,\n  procurement_plan_entries.total_ifb_quantity,\n  procurement_plan_entries.currency,\n  procurement_types.title AS procurement_type,\n  procurement_methods.title AS procurement_method,\n  funding_sources.title AS funding_source,\n  pdes.pdename\n \n   \n  \n  FROM procurement_plans\n  INNER JOIN procurement_plan_entries\n    ON procurement_plans.id = procurement_plan_entries.procurement_plan_id\n  INNER JOIN procurement_types\n    ON procurement_plan_entries.procurement_type = procurement_types.id\n  INNER JOIN procurement_methods\n    ON procurement_plan_entries.procurement_method = procurement_methods.id\n  INNER JOIN funding_sources\n    ON procurement_plan_entries.funding_source = funding_sources.id\n  INNER JOIN pdes\n    ON procurement_plans.pde_id = pdes.pdeid\n  LEFT OUTER JOIN\n    bidinvitations \n  ON bidinvitations.procurement_id = procurement_plan_entries.id\n  \n\n WHERE _SEARCHSTRING_   ORDER BY _ORDERBY_ _LIMITTEXT_', '2015-06-05 00:00:00'),
(709, 'search_bidinvitation', 'Search Bid Invitation Existance', 'SELECT * FROM \r\n  bidinvitations A\r\n  INNER JOIN \r\n  procurement_plan_entries B\r\n  ON A.procurement_id = B.id\r\n  WHERE \r\n  _SEARCHSTRING_ \r\n  ORDER BY\r\n  _ORDERBY_ _LIMITTEXT_', '2015-06-13 00:00:00');
